"use client";

import React, { useEffect, useMemo, useRef, useState } from "react";
import { api } from "~/trpc/react";
import { FocusTrap, useReturnFocus } from "./focus-trap";
import type { RouterOutputs } from "~/trpc/react";

type RightSwipeAction = "collapse_expand" | "none";

interface PreferencesModalProps {
  open: boolean;
  onClose: () => void;
}

export function PreferencesModal({ open, onClose }: PreferencesModalProps) {
  const utils = api.useUtils();
  const { data: prefs, isLoading } = api.preferences.get.useQuery(undefined, {
    enabled: open,
  });

  const updateMutation = api.preferences.update.useMutation({
    onSuccess: async () => {
      await utils.preferences.get.invalidate();
    },
  });

  const [predictiveEnabled, setPredictiveEnabled] = useState<boolean>(false);
  const [rightSwipeAction, setRightSwipeAction] = useState<RightSwipeAction>("collapse_expand");
  const [estimatedOneRmFactor, setEstimatedOneRmFactor] = useState<string>(""); // text to allow blank => default
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    if (!isLoading && prefs) {
      // Server returns shape with safe defaults; guard for union variants
      const predictive =
        "predictive_defaults_enabled" in prefs
          ? Boolean(prefs.predictive_defaults_enabled ?? false)
          : false;
      setPredictiveEnabled(predictive);

      const rightSwipe =
        "right_swipe_action" in prefs
          ? (prefs.right_swipe_action ?? "collapse_expand")
          : "collapse_expand";
      setRightSwipeAction(rightSwipe as RightSwipeAction);

      // Only set estimatedOneRmFactor if present
      const factor =
        "estimated_one_rm_factor" in prefs
          ? prefs.estimated_one_rm_factor
          : undefined;
      setEstimatedOneRmFactor(typeof factor === "number" ? String(factor) : "");
    }
  }, [isLoading, prefs]);

  const saveDisabled = useMemo(() => {
    if (!prefs) return false; // allow initial save
    const pe =
      "predictive_defaults_enabled" in prefs
        ? Boolean(prefs.predictive_defaults_enabled ?? false)
        : false;
    const rs =
      "right_swipe_action" in prefs
        ? ((prefs.right_swipe_action ?? "collapse_expand") as RightSwipeAction)
        : ("collapse_expand" as RightSwipeAction);
    const pf =
      "estimated_one_rm_factor" in prefs
        ? prefs.estimated_one_rm_factor
        : undefined;
    const uiPf =
      estimatedOneRmFactor.trim() === "" ? undefined : Number(estimatedOneRmFactor);
    return pe === predictiveEnabled && rs === rightSwipeAction && (pf ?? undefined) === (uiPf ?? undefined);
  }, [prefs, predictiveEnabled, rightSwipeAction, estimatedOneRmFactor]);

  const handleSave = async () => {
    try {
      setSaving(true);
      const payload: {
        predictive_defaults_enabled?: boolean;
        right_swipe_action?: RightSwipeAction;
        estimated_one_rm_factor?: number;
      } = {
        predictive_defaults_enabled: predictiveEnabled,
        right_swipe_action: rightSwipeAction,
      };
      // Only send factor if the input is not blank and within bounds; otherwise omit to keep default
      const trimmed = estimatedOneRmFactor.trim();
      if (trimmed !== "") {
        const n = Number(trimmed);
        if (!Number.isNaN(n)) {
          payload.estimated_one_rm_factor = Math.min(0.05, Math.max(0.02, n));
        }
      }
      await updateMutation.mutateAsync(payload);
      onClose();
    } finally {
      setSaving(false);
    }
  };

  // Always call hooks in the same order; avoid returning early before hooks.
  // We render null at the end when not open.
  const { restoreFocus } = useReturnFocus();
  const firstFocusRef = useRef<HTMLButtonElement>(null);

  const isClosed = !open;

  if (isClosed) {
    // When closed, render a stable, minimal subtree (no early return before hooks).
    return null;
  }

  return (
    <div
      className="fixed inset-0 z-[10000] flex items-end sm:items-center justify-center p-0 sm:p-4 bg-black/60"
      role="dialog"
      aria-modal="true"
      aria-labelledby="preferences-title"
      onClick={() => {
        restoreFocus();
        onClose();
      }}
    >
      <FocusTrap
        onEscape={() => {
          restoreFocus();
          onClose();
        }}
        initialFocusRef={firstFocusRef as React.RefObject<HTMLElement>}
        preventScroll
      >
        <div
          className="w-full sm:max-w-md sm:rounded-xl sm:shadow-2xl sm:bg-[var(--card)] sm:border sm:border-[var(--border)] bg-[var(--card)] border-t border-[var(--border)]"
          onClick={(e) => e.stopPropagation()}
        >
          <div className="px-4 py-3 sm:px-6 sm:py-4 border-b border-[var(--border)]">
            <h2 id="preferences-title" className="text-lg font-bold">
              Preferences
            </h2>
          </div>

        <div className="px-4 py-3 sm:px-6 sm:py-5 space-y-6">
          {/* Predictive defaults toggle */}
          <section>
            <div className="flex items-center justify-between">
              <div>
                <div className="font-medium">Predictive defaults</div>
                <div className="text-sm text-muted">
                  Prefill new sets with your most recent values for the exercise.
                </div>
              </div>
              <button
                type="button"
                aria-pressed={predictiveEnabled ? "true" : "false"}
                onClick={() => setPredictiveEnabled((v) => !v)}
                className={`inline-flex h-8 w-14 items-center rounded-full transition-colors ${
                  predictiveEnabled ? "bg-purple-600" : "bg-gray-600"
                }`}
              >
                <span
                  className={`inline-block h-6 w-6 transform rounded-full bg-white transition-transform ${
                    predictiveEnabled ? "translate-x-7" : "translate-x-1"
                  }`}
                />
                <span className="sr-only">Toggle predictive defaults</span>
              </button>
            </div>
          </section>

          {/* Estimated 1RM factor */}
          <section>
            <div className="font-medium mb-1">Estimated 1RM factor</div>
            <div className="text-sm text-muted mb-2">
              Used in 1RM estimation formula: weight × (1 + reps × factor). Leave blank to use default 0.0333 (1/30).
            </div>
            <div className="flex items-center gap-3">
              <input
                type="number"
                step="0.0001"
                min={0.02}
                max={0.05}
                inputMode="decimal"
                aria-label="Estimated 1RM factor"
                className="w-32 rounded-md border border-[var(--border)] bg-[var(--card)] px-3 py-2"
                placeholder="0.0333"
                value={estimatedOneRmFactor}
                onChange={(e) => setEstimatedOneRmFactor(e.target.value)}
              />
              <div className="text-xs text-muted">
                Default: 0.0333
              </div>
            </div>
          </section>

          {/* Right swipe action selector */}
          <section>
            <div className="font-medium mb-1">Right-swipe action</div>
            <div className="text-sm text-muted mb-2">
              Choose what happens when you right-swipe an exercise card.
            </div>
            <div className="grid grid-cols-2 gap-2">
              {(["collapse_expand", "none"] as RightSwipeAction[]).map((opt) => (
                <button
                  key={opt}
                  onClick={() => setRightSwipeAction(opt)}
                  className={`rounded-lg border px-3 py-2 text-sm ${
                    rightSwipeAction === opt
                      ? "btn-primary"
                      : "btn-secondary"
                  }`}
                  aria-pressed={rightSwipeAction === opt ? "true" : "false"}
                >
                  {opt === "collapse_expand" ? "Collapse/Expand" : "None"}
                </button>
              ))}
            </div>
          </section>

          {/* Asymmetric swipe thresholds placeholder */}
          <section className="rounded-md border border-dashed border-[var(--border)] p-3">
            <div className="font-medium">Asymmetric swipe thresholds</div>
            <div className="text-sm text-muted">
              Coming soon: configure different swipe distances for left/right actions.
            </div>
          </section>
        </div>

          <div className="px-4 py-3 sm:px-6 sm:py-4 border-t border-[var(--border)] flex gap-2 justify-end">
            <button
              ref={firstFocusRef}
              onClick={() => {
                restoreFocus();
                onClose();
              }}
              className="btn-secondary px-4 py-2"
              disabled={saving}
            >
              Cancel
            </button>
            <button
              onClick={() => void handleSave()}
              className="btn-primary px-4 py-2 disabled:opacity-50"
              disabled={saving || saveDisabled}
              aria-busy={saving ? "true" : "false"}
            >
              {saving ? "Saving…" : "Save"}
            </button>
          </div>
        </div>
      </FocusTrap>
    </div>
  );
}

"use client";

import { useRef } from "react";
import { FocusTrap, useReturnFocus } from "./focus-trap";

interface ProgressionScopeModalProps {
  isOpen: boolean;
  onClose: () => void;
  progressionType: "weight" | "reps";
  increment: string; // e.g., "+2.5kg" or "+1 rep"
  onApplyToAll: () => void;
  onApplyToHighest: () => void;
}

export function ProgressionScopeModal({
  isOpen,
  onClose,
  progressionType,
  increment,
  onApplyToAll,
  onApplyToHighest,
}: ProgressionScopeModalProps) {
  // Always call hooks unconditionally at the top of the component
  const { restoreFocus } = useReturnFocus();
  const firstFocusRef = useRef<HTMLButtonElement>(null);

  if (!isOpen) return null;

  return (
    <div
      className="fixed inset-0 z-50 flex items-center justify-center modal-backdrop"
      role="dialog"
      aria-modal="true"
      aria-labelledby="progression-scope-title"
      onClick={() => {
        restoreFocus();
        onClose();
      }}
    >
      <FocusTrap
        onEscape={() => {
          restoreFocus();
          onClose();
        }}
        initialFocusRef={firstFocusRef as React.RefObject<HTMLElement>}
        preventScroll
      >
        <div className="card glass-surface p-6 max-w-md w-full mx-4" onClick={(e) => e.stopPropagation()}>
          <h3 id="progression-scope-title" className="text-xl font-semibold mb-4">
            Apply Progression
          </h3>

          <p className="text-secondary mb-6">
            You chose to add <span className="font-medium text-green-700 dark:text-green-400">{increment}</span>.
            <br />
            How would you like to apply this progression?
          </p>

        <div className="space-y-3">
          <button
            onClick={() => {
              onApplyToAll();
              onClose();
            }}
            className="w-full p-3 text-left rounded-lg btn-success transition-colors"
          >
            <div className="font-medium">Apply to ALL sets</div>
            <div className="text-sm opacity-90">
              Every set gets {increment}
            </div>
          </button>

          <button
            onClick={() => {
              onApplyToHighest();
              onClose();
            }}
            className="w-full p-3 text-left rounded-lg btn-primary transition-colors"
          >
            <div className="font-medium">Apply to HIGHEST set only</div>
            <div className="text-sm opacity-90">
              Only the heaviest set gets {increment}
            </div>
          </button>
        </div>

          <button
            ref={firstFocusRef}
            onClick={() => {
              restoreFocus();
              onClose();
            }}
            className="mt-4 w-full rounded-lg btn-secondary px-4 py-2 text-sm"
          >
            Cancel
          </button>
        </div>
      </FocusTrap>
    </div>
  );
}

"use client";

import { useState, useEffect, useRef } from "react";
import Link from "next/link";
import { api } from "~/trpc/react";
import { WorkoutDetailOverlay } from "./workout-detail-overlay";
import { useLocalStorage } from "~/hooks/use-local-storage";
import { useWorkoutUpdates } from "~/hooks/use-workout-updates";

export function WhoopWorkouts() {
  const [syncLoading, setSyncLoading] = useState(false);
  const [message, setMessage] = useState<{ type: "success" | "error"; text: string } | null>(null);
  const [rateLimit, setRateLimit] = useState<{ remaining: number; resetTime: string } | null>(null);
  const [selectedWorkout, setSelectedWorkout] = useState<any>(null);
  const [clickOrigin, setClickOrigin] = useState<{ x: number; y: number } | undefined>();

  // Use custom localStorage hook for persistent preferences
  const [showAll, setShowAll, isShowAllLoaded] = useLocalStorage('whoop-workouts-show-all', false);
  const [sportFilter, setSportFilter, isSportFilterLoaded] = useLocalStorage('whoop-workouts-sport-filter', 'all');

  // Debug function to clear localStorage (temporary)
  const clearPreferences = () => {
    if (typeof window !== 'undefined') {
      localStorage.removeItem('whoop-workouts-sport-filter');
      localStorage.removeItem('whoop-workouts-show-all');
      window.location.reload();
    }
  };

  const { data: integrationStatus } = api.whoop.getIntegrationStatus.useQuery();
  const { data: workouts, refetch: refetchWorkouts, isLoading: workoutsLoading } = api.whoop.getWorkouts.useQuery();

  // Listen for real-time workout updates via SSE
  useWorkoutUpdates(() => {
    // Refetch workouts when we receive an update
    void refetchWorkouts();
  });

  const handleSync = async () => {
    setSyncLoading(true);
    setMessage(null);

    try {
      const response = await fetch("/api/whoop/sync-workouts", {
        method: "POST",
      });

      const result = await response.json();

      if (response.ok) {
        setMessage({
          type: "success",
          text: `Sync completed! ${result.newWorkouts} new workouts, ${result.duplicates} duplicates skipped.`,
        });
        setRateLimit(result.rateLimit);
        void refetchWorkouts();
      } else if (response.status === 429) {
        setMessage({ 
          type: "error", 
          text: result.message || "Rate limit exceeded. Please try again later." 
        });
        setRateLimit({ remaining: 0, resetTime: result.resetTime });
      } else {
        setMessage({ type: "error", text: result.error || "Sync failed" });
      }
    } catch (error) {
      setMessage({ type: "error", text: "Network error during sync" });
    } finally {
      setSyncLoading(false);
    }
  };

  const formatDateTime = (start: Date, end: Date) => {
    const startTime = new Intl.DateTimeFormat("en-GB", {
      day: "numeric",
      month: "short",
      hour: "numeric",
      minute: "2-digit",
      hour12: true,
    }).format(start);

    const endTime = new Intl.DateTimeFormat("en-GB", {
      hour: "numeric",
      minute: "2-digit",
      hour12: true,
    }).format(end);

    return `${startTime} - ${endTime}`;
  };

  const formatScore = (score: any, scoreState: string | null) => {
    if (!score || typeof score !== "object") {
      return `-- (${scoreState?.replace("_", " ") || "UNKNOWN"})`;
    }

    const strainScore = (score)?.strain;
    if (strainScore && typeof strainScore === "number") {
      return `${strainScore.toFixed(1)} (${scoreState?.replace("_", " ") || "UNKNOWN"})`;
    }

    return `-- (${scoreState?.replace("_", " ") || "UNKNOWN"})`;
  };

  // Get unique sports for filter dropdown
  const uniqueSports = workouts ? [...new Set(workouts.map(w => w.sport_name).filter((sport): sport is string => sport !== null && sport !== undefined))] : [];

  // Filter and sort workouts by start time (latest first)
  const filteredWorkouts = workouts ? workouts.filter(workout => 
    sportFilter === "all" || workout.sport_name === sportFilter
  ) : [];

  const sortedWorkouts = [...filteredWorkouts].sort((a, b) => 
    new Date(b.start).getTime() - new Date(a.start).getTime()
  );

  const displayedWorkouts = showAll ? sortedWorkouts : sortedWorkouts.slice(0, 3);

  const handleWorkoutClick = (workout: any, event: React.MouseEvent) => {
    const rect = event.currentTarget.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;

    setClickOrigin({ x: centerX, y: centerY });
    setSelectedWorkout(workout);
  };

  return (
    <div className="space-y-8">
      {/* Sync Controls */}
      <div className="flex justify-center">
        <div className="max-w-md rounded-lg card p-6 text-center">
          {integrationStatus?.isConnected ? (
            <div className="space-y-4">
              <div className="text-green-700 dark:text-green-400">
                ✅ Connected to Whoop
              </div>
              <button
                onClick={handleSync}
                disabled={syncLoading || (rateLimit?.remaining === 0)}
                className="btn-primary w-full px-6 py-3 disabled:opacity-50"
              >
                {syncLoading ? "Syncing..." : "Sync with Whoop"}
              </button>
              {rateLimit && (
                <div className="text-xs text-secondary text-center">
                  {rateLimit.remaining > 0 ? (
                    `${rateLimit.remaining} syncs remaining this hour`
                  ) : (
                    `Rate limit reached. Resets at ${new Date(rateLimit.resetTime).toLocaleTimeString()}`
                  )}
                </div>
              )}
            </div>
          ) : (
            <div className="space-y-4">
              <div className="text-red-600 dark:text-red-400">
                ❌ Not connected to Whoop
              </div>
              <Link
                href="/connect-whoop"
                className="btn-primary block w-full px-6 py-3 text-center"
              >
                Connect Whoop Now
              </Link>
            </div>
          )}
        </div>
      </div>

      {/* Messages */}
      {message && (
        <div className="flex justify-center">
          <div
            className={`max-w-md rounded-lg p-4 border ${
              message.type === "success"
                ? "bg-green-50 text-green-800 border-green-300 dark:bg-green-900/30 dark:text-green-100 dark:border-green-700"
                : "bg-red-50 text-red-800 border-red-300 dark:bg-red-900/30 dark:text-red-100 dark:border-red-700"
            }`}
          >
            {message.text}
          </div>
        </div>
      )}

      {/* Workouts Display */}
      {integrationStatus?.isConnected && (
        <div>
          <div className="mb-6">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-2xl font-semibold">Your Workouts</h2>
              {sortedWorkouts.length > 3 && (
                <button
                  onClick={() => setShowAll(!showAll)}
                  className="text-sm link-primary no-underline"
                >
                  {showAll ? "Show Less" : `Show All (${sortedWorkouts.length})`}
                </button>
              )}
            </div>

            {/* Sport Filter */}
            {uniqueSports.length > 0 && (
              <div className="flex items-center gap-3">
                <label htmlFor="sport-filter" className="text-sm text-secondary">
                  Filter by sport:
                </label>
                <select
                  id="sport-filter"
                  value={sportFilter}
                  onChange={(e) => setSportFilter(e.target.value)}
                  className="rounded-lg border border-gray-200 bg-white px-3 py-2 text-sm text-gray-900 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-colors dark:border-gray-600 dark:bg-gray-700 dark:text-white"
                >
                  <option value="all">All Sports</option>
                  {uniqueSports.map((sport) => (
                    <option key={sport} value={sport}>
                      {sport}
                    </option>
                  ))}
                </select>
                <button
                  onClick={clearPreferences}
                  className="text-xs px-2 py-1 rounded border border-red-300 text-red-700 hover:bg-red-50 dark:border-red-500 dark:text-red-300 dark:hover:bg-red-900/30"
                  title="Clear corrupted localStorage data"
                >
                  Reset Prefs
                </button>
              </div>
            )}
          </div>

          {workoutsLoading ? (
            <div className="text-center py-8">
              <p className="text-secondary">Loading workouts...</p>
            </div>
          ) : displayedWorkouts.length > 0 ? (
            <div className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
              {displayedWorkouts.map((workout) => (
                <div
                  key={workout.id}
                  className="card p-6 transition-all duration-200 cursor-pointer hover:shadow-lg hover:scale-105"
                  onClick={(e) => handleWorkoutClick(workout, e)}
                >
                  <div className="space-y-3">
                    <div className="text-sm text-muted">
                      {formatDateTime(new Date(workout.start), new Date(workout.end))}
                    </div>

                    <h3 className="text-lg font-semibold">
                      {workout.sport_name || "Unknown Sport"}
                    </h3>

                    <div className="text-sm text-secondary">
                      <span className="font-medium">Score:</span> {formatScore(workout.score, workout.score_state)}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8">
              <p className="text-secondary">
                {sportFilter !== "all" && sortedWorkouts.length === 0 
                  ? "No workouts from whoop" 
                  : "No workouts found. Click \"Sync with Whoop\" to fetch your data."}
              </p>
            </div>
          )}
        </div>
      )}



      {/* Workout Detail Overlay */}
      <WorkoutDetailOverlay
        workout={selectedWorkout}
        isOpen={!!selectedWorkout}
        onClose={() => setSelectedWorkout(null)}
        clickOrigin={clickOrigin}
      />
    </div>
  );
}

"use client";

import { useState, useEffect, useRef } from "react";
import { useRouter } from "next/navigation";
import Link from "next/link";
import { api } from "~/trpc/react";
import { ExerciseCard, type ExerciseData } from "./exercise-card";
import { type SetData } from "./set-input";
import { ProgressionModal } from "./progression-modal";
import { ProgressionScopeModal } from "./progression-scope-modal";
import { Toast } from "./ui/Toast";
import { useLiveRegion, useAttachLiveRegion } from "./LiveRegion";
import { FocusTrap, useReturnFocus } from "./focus-trap";

interface PreviousBest {
  weight?: number;
  reps?: number;
  sets?: number;
  unit: "kg" | "lbs";
}
import { analytics } from "~/lib/analytics";
import posthog from "posthog-js";
import { vibrate, getDeviceType, getThemeUsed, snapshotMetricsBlob } from "~/lib/client-telemetry";
import { useCacheInvalidation } from "~/hooks/use-cache-invalidation";
import { useUniversalDragReorder } from "~/hooks/use-universal-drag-reorder";
import { type SwipeSettings } from "~/hooks/use-swipe-gestures";
import { useOfflineSaveQueue } from "~/hooks/use-offline-save-queue";
import { useWorkoutSessionState } from "~/hooks/useWorkoutSessionState";

interface WorkoutSessionProps {
  sessionId: number;
}

export function WorkoutSession({ sessionId }: WorkoutSessionProps) {
  const router = useRouter();
  const { onWorkoutSave, invalidateWorkouts } = useCacheInvalidation();

  // Move complex state and effects into a dedicated hook to reduce component size.
  const {
    exercises,
    setExercises,
    expandedExercises,
    setExpandedExercises,
    loading,
    isReadOnly,
    showDeleteConfirm,
    setShowDeleteConfirm,
    previousExerciseData,
    notification,
    setNotification,
    collapsedIndexes,
    progressionModal,
    setProgressionModal,
    hasShownAutoProgression,
    setHasShownAutoProgression,
    progressionScopeModal,
    setProgressionScopeModal,
    saveWorkout,
    deleteWorkout,
    enqueue,
    flush,
    queueSize,
    isFlushing,
    swipeSettings,
    dragState,
    dragHandlers,
    getDisplayOrder,
    toggleExpansion,
    handleSwipeToBottom,
    updateSet: hookUpdateSet,
    toggleUnit: hookToggleUnit,
    addSet: hookAddSet,
    deleteSet: hookDeleteSet,
    buildSavePayload,
    session,
    updatePreferences,
    // undo integration
    lastAction,
    undoLastAction,
    setLastAction,
  } = useWorkoutSessionState({ sessionId });

  // Generate unique ID for sets
  // id generation handled in hook

  // Swipe settings for all exercise cards - adjusted for slower, more controlled feel
  // swipeSettings provided by hook

  // Get the display order of exercises (normal exercises first, then swiped to bottom)
  // getDisplayOrder provided by hook

  // Universal drag and drop functionality - works on both mobile and desktop
  // drag state/handlers provided by hook
  const displayOrder = getDisplayOrder();

  // Conservative virtualization window for very large templates
  const VIRTUALIZE_THRESHOLD = 20;
  const WINDOW_BEFORE = 6;
  const WINDOW_AFTER = 6;

  const [scrollY, setScrollY] = useState(0);
  const listContainerRef = useRef<HTMLDivElement | null>(null);

  useEffect(() => {
    const onScroll = () => setScrollY(window.scrollY || 0);
    window.addEventListener("scroll", onScroll, { passive: true });
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const getItemTop = (index: number) => {
    // Approximate per-card height; lightweight and avoids layout thrash. Cards are fairly uniform in this view.
    const approx = 120; // px
    return index * approx;
  };

  const totalCount = displayOrder.length;
  const shouldVirtualize = totalCount >= VIRTUALIZE_THRESHOLD;

  const viewportHeight = typeof window !== "undefined" ? window.innerHeight : 800;
  const startIndex = shouldVirtualize ? Math.max(0, Math.floor(scrollY / 120) - WINDOW_BEFORE) : 0;
  const endIndex = shouldVirtualize ? Math.min(totalCount - 1, Math.ceil((scrollY + viewportHeight) / 120) + WINDOW_AFTER) : totalCount - 1;

  // Accessibility live region
  const announce = useLiveRegion();
  useAttachLiveRegion(announce);

  // previous data loading handled in hook

  // session initialization handled in hook

  // auto-progression modal logic handled in hook

  // ===== COMPLETE WORKOUT MODAL STATE =====
  const [showCompleteModal, setShowCompleteModal] = useState(false);
  // Focus restore for inline modals
  const { restoreFocus: restoreFocusInline } = useReturnFocus();

  type BestMetrics = {
    bestWeight?: { weight: number; reps?: number; sets?: number; unit: "kg" | "lbs" };
    bestVolume?: { volume: number; weight?: number; reps?: number; unit: "kg" | "lbs" };
  };

  const computeCurrentBest = (ex: ExerciseData): BestMetrics => {
    if (!ex.sets || ex.sets.length === 0) return {};
    // Max weight, tie-break by reps
    const maxWeight = Math.max(...ex.sets.map((s) => s.weight ?? 0));
    const weightCandidates = ex.sets.filter((s) => (s.weight ?? 0) === maxWeight);
    const bestByWeight = weightCandidates.sort((a, b) => (b.reps ?? 0) - (a.reps ?? 0))[0];
    // Max volume (weight * reps)
    const withVolume = ex.sets
      .map((s) => ({ ...s, volume: (s.weight ?? 0) * (s.reps ?? 0) }))
      .filter((s) => s.volume && s.volume > 0);
    const bestByVolume = withVolume.sort((a, b) => (b.volume - a.volume))[0];

    return {
      bestWeight: bestByWeight?.weight
        ? { weight: bestByWeight.weight, reps: bestByWeight.reps, sets: bestByWeight.sets, unit: bestByWeight.unit }
        : undefined,
      bestVolume: bestByVolume
        ? { volume: bestByVolume.volume, weight: bestByVolume.weight, reps: bestByVolume.reps, unit: bestByVolume.unit }
        : undefined,
    };
  };

  const openCompleteModal = () => {
    setShowCompleteModal(true);
  };
  const closeCompleteModal = () => {
    setShowCompleteModal(false);
  };

  // thin wrappers to call hook helpers (keeps JSX unchanged)
  const updateSet = hookUpdateSet;
  const toggleUnit = hookToggleUnit;
  const addSet = hookAddSet;
  const vibrateSafe = (pattern: number | number[]) => {
    try {
      vibrate(pattern);
    } catch {}
  };
  const deleteSet = hookDeleteSet;

  // toggleExpansion provided by hook

  // use handler from hook
  // handleSwipeToBottom provided by hook

  const handleProgressionChoice = (type: "weight" | "reps" | "none") => {
    if (!progressionModal) {
      return;
    }

    const { exerciseIndex, previousBest } = progressionModal;

    if (type === "none") {
      // No progression, just expand and close
      setExpandedExercises(prev => {
        if (!prev.includes(exerciseIndex)) {
          return [...prev, exerciseIndex];
        }
        return prev;
      });
      setProgressionModal(null);
      return;
    }

    // For weight/reps progression, show scope selection modal
    const increment = type === "weight" 
      ? `+${previousBest.unit === "kg" ? "2.5" : "5"}${previousBest.unit}`
      : "+1 rep";

    setProgressionScopeModal({
      isOpen: true,
      exerciseIndex,
      progressionType: type,
      increment,
      previousBest,
    });

    // Close first modal
    setProgressionModal(null);
  };

  const applyProgressionToAll = () => {
    if (!progressionScopeModal) return;

    const { exerciseIndex, progressionType, previousBest } = progressionScopeModal;

    setExercises(prev => {
      const newExercises = [...prev];
      const exercise = newExercises[exerciseIndex];

      if (exercise) {
        // Apply progression to ALL sets
        exercise.sets = exercise.sets.map(set => {
          const updatedSet = { ...set };

          if (progressionType === "weight" && updatedSet.weight) {
            const increment = updatedSet.unit === "kg" ? 2.5 : 5;
            updatedSet.weight += increment;
          } else if (progressionType === "reps" && updatedSet.reps) {
            updatedSet.reps += 1;
          }

          return updatedSet;
        });
      }

      return newExercises;
    });

    // Expand the exercise
    setExpandedExercises(prev => {
      if (!prev.includes(exerciseIndex)) {
        return [...prev, exerciseIndex];
      }
      return prev;
    });
  };

  const applyProgressionToHighest = () => {
    if (!progressionScopeModal) return;

    const { exerciseIndex, progressionType, previousBest } = progressionScopeModal;

    setExercises(prev => {
      const newExercises = [...prev];
      const exercise = newExercises[exerciseIndex];

      if (exercise) {
        // Find the set that matches the previous best performance
        const bestSetIndex = exercise.sets.findIndex(set => 
          set.weight === previousBest.weight &&
          set.reps === previousBest.reps &&
          set.sets === previousBest.sets
        );

        if (bestSetIndex !== -1) {
          const updatedSet = { ...exercise.sets[bestSetIndex]! };

          if (progressionType === "weight" && updatedSet.weight) {
            const increment = updatedSet.unit === "kg" ? 2.5 : 5;
            updatedSet.weight += increment;
          } else if (progressionType === "reps" && updatedSet.reps) {
            updatedSet.reps += 1;
          }

          exercise.sets[bestSetIndex] = updatedSet;
        }
      }

      return newExercises;
    });

    // Expand the exercise
    setExpandedExercises(prev => {
      if (!prev.includes(exerciseIndex)) {
        return [...prev, exerciseIndex];
      }
      return prev;
    });
  };

  // buildSavePayload provided by hook

  const handleSave = async () => {
    // Validate that exercises have required data
    const validationErrors: string[] = [];

    exercises.forEach((exercise, exerciseIndex) => {
      exercise.sets.forEach((set, setIndex) => {
        const hasData = set.weight !== undefined || set.reps !== undefined || (set.sets && set.sets > 0);

        if (hasData) {
          // If the set has some data, validate that numeric fields are proper numbers
          if (set.weight !== undefined && (set.weight === null || isNaN(set.weight))) {
            validationErrors.push(`${exercise.exerciseName}, Set ${setIndex + 1}: Weight must be a valid number`);
          }
          if (set.reps !== undefined && (set.reps === null || isNaN(set.reps) || set.reps <= 0)) {
            validationErrors.push(`${exercise.exerciseName}, Set ${setIndex + 1}: Reps must be a valid positive number`);
          }
          if (set.sets !== undefined && (set.sets === null || isNaN(set.sets) || set.sets <= 0)) {
            validationErrors.push(`${exercise.exerciseName}, Set ${setIndex + 1}: Sets must be a valid positive number`);
          }
        }
      });
    });

    if (validationErrors.length > 0) {
      setNotification({
        type: "error",
        message: `Please fix the following errors before saving:\n${validationErrors.join('\n')}`
      });
      setTimeout(() => setNotification(null), 8000); // Auto-dismiss after 8 seconds
      return;
    }

    try {
      const payload = buildSavePayload();

      // If offline, enqueue and notify
      if (typeof navigator !== "undefined" && navigator.onLine === false) {
        enqueue(payload);
        setNotification({
          type: "success",
          message:
            "You’re offline. Workout queued and will sync automatically when back online. You can also tap 'Sync now' in the status bar.",
        });
        // No navigation here; let user remain on page
        setTimeout(() => setNotification(null), 6000);
        return;
      }

      // Try online save
      await saveWorkout.mutateAsync(payload);

      // Show success notification briefly before navigation
      setNotification({
        type: "success",
        message: "Workout saved successfully!",
      });
    } catch (error) {
      console.error("Error saving workout:", error);
      analytics.error(error as Error, {
        context: "workout_save",
        sessionId: sessionId.toString(),
      });

      // If likely a network error, enqueue for offline
      const message =
        error && typeof error === "object" && "message" in error
          ? String((error as Error).message)
          : "";

      if (
        message.includes("Failed to fetch") ||
        message.includes("NetworkError") ||
        message.includes("TypeError")
      ) {
        const payload = buildSavePayload();
        enqueue(payload);
        setNotification({
          type: "success",
          message:
            "Network issue detected. Workout queued and will sync automatically on reconnect.",
        });
        setTimeout(() => setNotification(null), 6000);
        return;
      }

      // Otherwise surface validation/unknown error
      if (message.includes("Expected number, received null") || message.includes("invalid_type")) {
        setNotification({
          type: "error",
          message:
            "Please make sure all exercise fields are properly filled out. Empty fields should be left blank, not contain invalid values.",
        });
      } else {
        setNotification({
          type: "error",
          message: `Error saving workout: ${message || "Unknown error"}`,
        });
      }
      setTimeout(() => setNotification(null), 6000);
    }
  };

  const handleDelete = async () => {
    try {
      await deleteWorkout.mutateAsync({ id: sessionId });
    } catch (error) {
      console.error("Error deleting workout:", error);
      analytics.error(error as Error, {
        context: "workout_delete",
        sessionId: sessionId.toString(),
      });
      alert("Error deleting workout. Please try again.");
    }
  };

  if (loading || !session) {
    return (
      <div className="space-y-4">
        {[...(Array(3) as number[])].map((_, i) => (
          <div key={i} className="animate-pulse rounded-lg bg-gray-800 p-4">
            <div className="mb-4 h-4 w-1/2 rounded bg-gray-700"></div>
            <div className="grid grid-cols-3 gap-3">
              <div className="h-10 rounded bg-gray-700"></div>
              <div className="h-10 rounded bg-gray-700"></div>
              <div className="h-10 rounded bg-gray-700"></div>
            </div>
          </div>
        ))}
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {/* Notification */}
      {notification && (
        <div
          role="status"
          aria-live={notification.type === "error" ? "assertive" : "polite"}
          aria-atomic="true"
          className={`sticky top-4 z-50 rounded-lg p-4 shadow-lg ${
            notification.type === "error" 
              ? "bg-red-900 border border-red-700 text-red-100" 
              : "bg-green-900 border border-green-700 text-green-100"
          }`}
        >
          <div className="flex items-center justify-between">
            <div className="whitespace-pre-line">{notification.message}</div>
            <button
              onClick={() => setNotification(null)}
              aria-label="Dismiss notification"
              className="ml-4 text-lg font-bold opacity-70 hover:opacity-100"
            >
              ×
            </button>
          </div>
        </div>
      )}

      {/* Persistent Undo (global) */}
      {lastAction && (
        <div className="sticky bottom-4 z-[60]">
          <Toast
            open={true}
            type="info"
            message={
              lastAction.type === "swipeToEnd"
                ? "Exercise moved to end"
                : lastAction.type === "toggleCollapse"
                ? "Exercise expanded state changed"
                : "Order changed"
            }
            onUndo={() => {
              undoLastAction();
            }}
            onClose={() => setLastAction(null)}
          />
        </div>
      )}

      {/* Gesture Help (only show if not read-only and has exercises) */}
      {!isReadOnly && exercises.length > 0 && (
        <div className="text-center text-sm text-muted mb-2">
          💡 <strong>Tip:</strong> Swipe ← → to move to bottom • Drag ↕ to reorder & move between sections • Works on mobile & desktop
        </div>
      )}

      {/* Exercise Cards */}
      {(shouldVirtualize
        ? displayOrder.slice(startIndex, endIndex + 1).map((entry, i) => ({ ...entry, windowIndex: startIndex + i }))
        : displayOrder.map((entry, i) => ({ ...entry, windowIndex: i }))
      ).map(({ exercise, originalIndex, windowIndex }) => {
        const displayIndex = windowIndex;
        // Collapsed state is derived from collapsedIndexes mapped to current order
        const isCollapsed = collapsedIndexes.includes(displayIndex);
        const isExpandedNow = !isCollapsed && expandedExercises.includes(originalIndex);


        return (
          <div
            key={exercise.templateExerciseId ?? originalIndex}
            style={shouldVirtualize ? { minHeight: 0 } : undefined}
          >
            {/* Swiped Exercises Section Header */}
            <ExerciseCard
              exercise={exercise}
              exerciseIndex={originalIndex}
              onUpdate={updateSet}
              onToggleUnit={toggleUnit}
              onAddSet={addSet}
              onDeleteSet={deleteSet}
              isExpanded={isExpandedNow}
              onToggleExpansion={toggleExpansion}
              previousBest={previousExerciseData.get(exercise.exerciseName)?.best}
              previousSets={previousExerciseData.get(exercise.exerciseName)?.sets}
              readOnly={isReadOnly}
              onSwipeToBottom={handleSwipeToBottom}
              swipeSettings={swipeSettings}
              isSwiped={isCollapsed}
              draggable={!isReadOnly}
              isDraggedOver={dragState.dragOverIndex === displayIndex || dragState.dragOverIndex === displayIndex + 1}
              isDragging={dragState.isDragging && dragState.draggedIndex === displayIndex}
              dragOffset={dragState.dragOffset}
              onPointerDown={dragHandlers.onPointerDown(displayIndex)}
              setCardElement={(element) => dragHandlers.setCardElement?.(displayIndex, element)}
            />
          </div>
        );
      })}

      {/* No Exercises State */}
      {exercises.length === 0 && (
        <div className="py-8 text-center">
          <p className="text-secondary">No exercises in this template</p>
        </div>
      )}

      {/* Bottom action bar - editable only */}
      {!isReadOnly && (
        <div className="sticky bottom-4 z-50 pt-6" role="region" aria-label="Workout actions">
          <div className="glass-surface glass-hairline rounded-xl p-3 shadow-lg">
            <div className="grid grid-cols-3 gap-2">
              <button
                onClick={(e) => {
                  // Avoid double-fire from multiple pointer/mouse handlers or event bubbling
                  e.preventDefault();
                  e.stopPropagation();
                  if ((e as any).nativeEvent?._addSetHandled) return;
                  (e as any).nativeEvent._addSetHandled = true;

                  // Add a set to the first expanded exercise, or first exercise as fallback
                  const targetIndex =
                    expandedExercises[0] !== undefined ? expandedExercises[0] : 0;
                  if (typeof targetIndex === "number") {
                    addSet(targetIndex);
                    // clear undo since a new action happened
                    setLastAction(null);
                    // Haptic + PostHog
                    vibrateSafe(10);
                    try {
                      posthog.capture("haptic_action", { kind: "add_set" });
                    } catch {}
                    // Live announcement
                    try {
                      announce("Set added", { assertive: false });
                    } catch {}
                    // Smooth scroll to bottom to keep newly added set visible
                    try {
                      window.scrollTo({ top: document.body.scrollHeight, behavior: "smooth" });
                    } catch {}
                  }
                }}
                onPointerDown={(e) => e.stopPropagation()}
                onMouseDown={(e) => e.stopPropagation()}
                onTouchStart={(e) => e.stopPropagation()}
                className="btn-secondary py-3"
                aria-label="Add set to current exercise"
              >
                Add Set
              </button>

              <button
                onClick={async () => {
                  // PostHog-only telemetry snapshot at save tap
                  try {
                    const theme_used = getThemeUsed();
                    const device_type = getDeviceType();
                    const perf = snapshotMetricsBlob();
                    posthog.capture("workout_save", {
                      theme_used,
                      device_type,
                      tti: perf.tti,
                      tbt: perf.tbt,
                      input_latency_avg: (perf.inputLatency as any)?.avg,
                      input_latency_p95: (perf.inputLatency as any)?.p95,
                    });
                  } catch {}
                  await handleSave();
                  // Haptic feedback on save attempt (short-long-short)
                  vibrateSafe([10, 30, 10]);
                  try {
                    posthog.capture("haptic_action", { kind: "save" });
                  } catch {}
                  // Live announcement
                  try {
                    announce("Workout saved", { assertive: true });
                  } catch {}
                }}
                disabled={saveWorkout.isPending}
                className="btn-secondary py-3 disabled:opacity-50"
                aria-busy={saveWorkout.isPending ? "true" : "false"}
                aria-label="Save workout"
              >
                {saveWorkout.isPending ? "Saving…" : "Save"}
              </button>

              <button
                onClick={() => {
                  openCompleteModal();
                  // Optional subtle haptic to acknowledge opening modal
                  vibrateSafe(10);
                  try {
                    posthog.capture("haptic_action", { kind: "save" });
                  } catch {}
                }}
                className="btn-primary py-3"
              >
                Complete
              </button>
            </div>

            {/* Offline queue status row */}
            {queueSize > 0 && (
              <div className="mt-3 rounded-lg border p-2 text-xs flex items-center justify-between border-yellow-300 bg-yellow-50 text-yellow-800 dark:border-yellow-700 dark:bg-yellow-900/30 dark:text-yellow-100">
                <span>
                  {isFlushing
                    ? "Syncing queued workouts…"
                    : `${queueSize} workout${queueSize > 1 ? "s" : ""} pending sync`}
                </span>
                <button
                  onClick={() => {
                    try {
                      announce("Syncing queued workouts", { assertive: false });
                    } catch {}
                    void flush();
                  }}
                  className="ml-3 rounded border border-yellow-300 px-2 py-0.5 text-[11px] font-medium hover:bg-yellow-100 dark:border-yellow-600 dark:hover:bg-yellow-900/50"
                  aria-label="Sync queued workouts now"
                >
                  Sync now
                </button>
              </div>
            )}

            {/* Delete is secondary; keep outside the primary row */}
            <div className="mt-2 text-center">
              <button
                onClick={() => {
                  setShowDeleteConfirm(true);
                  setLastAction(null);
                  vibrateSafe(10);
                }}
                disabled={deleteWorkout.isPending}
                className="rounded-lg bg-red-600 px-4 py-2 text-sm font-semibold transition-colors hover:bg-red-700 disabled:opacity-50"
                aria-describedby="delete-workout-help"
              >
                {deleteWorkout.isPending ? "Deleting…" : "Delete Workout"}
              </button>
              <span id="delete-workout-help" className="sr-only">
                Opens a confirmation dialog
              </span>
            </div>
          </div>
        </div>
      )}

      {/* Read-only Actions */}
      {isReadOnly && (
        <div className="sticky bottom-4 space-y-3 pt-6">
          <Link
            href={`/workout/start?templateId=${session?.templateId}`}
            className="btn-primary block w-full py-3 text-center text-lg font-medium"
          >
            Repeat This Workout
          </Link>
          <button
            onClick={() => setShowDeleteConfirm(true)}
            disabled={deleteWorkout.isPending}
            className="w-full rounded-lg bg-red-600 py-3 text-lg font-medium transition-colors hover:bg-red-700 disabled:opacity-50"
          >
            {deleteWorkout.isPending ? "Deleting..." : "Delete Workout"}
          </button>
          <button
            onClick={() => router.back()}
            className="w-full rounded-lg bg-gray-700 py-3 text-lg font-medium transition-colors hover:bg-gray-600"
          >
            Back to History
          </button>
        </div>
      )}

      {/* Delete Confirmation Modal */}
      {showDeleteConfirm && (
        <div
          className="bg-opacity-75 fixed inset-0 z-[9999] flex items-center justify-center bg-black p-4"
          role="dialog"
          aria-modal="true"
          aria-labelledby="delete-workout-title"
          aria-describedby="delete-workout-desc"
          onClick={() => {
            restoreFocusInline();
            setShowDeleteConfirm(false);
          }}
        >
          <FocusTrap
            onEscape={() => {
              restoreFocusInline();
              setShowDeleteConfirm(false);
            }}
            preventScroll
          >
            <div
              className="w-full max-w-md card p-6 shadow-2xl"
              onClick={(e) => e.stopPropagation()}
            >
              <h3 id="delete-workout-title" className="mb-4 text-xl font-bold text-rose-600 dark:text-rose-400">
                Delete Workout
              </h3>
              <p id="delete-workout-desc" className="mb-6 leading-relaxed text-secondary">
                Are you sure you want to delete this workout?
                <br />
                <strong className="text-red-400">
                  This action cannot be undone.
                </strong>
              </p>
              <div className="flex gap-3">
                <button
                  onClick={() => {
                    restoreFocusInline();
                    setShowDeleteConfirm(false);
                  }}
                  className="flex-1 rounded-lg bg-gray-200 py-3 font-medium text-gray-900 transition-colors hover:bg-gray-300 dark:bg-gray-700 dark:text-white dark:hover:bg-gray-600"
                >
                  Cancel
                </button>
                <button
                  onClick={() => {
                    restoreFocusInline();
                    setShowDeleteConfirm(false);
                    void handleDelete();
                  }}
                  disabled={deleteWorkout.isPending}
                  className="flex-1 rounded-lg bg-red-600 py-3 font-medium text-white transition-colors hover:bg-red-700 disabled:opacity-50"
                >
                  {deleteWorkout.isPending ? "Deleting..." : "Yes, Delete"}
                </button>
              </div>
            </div>
          </FocusTrap>
        </div>
      )}

      {/* Progression Modal */}
      {progressionModal && (
        <ProgressionModal
          isOpen={progressionModal.isOpen}
          onClose={() => setProgressionModal(null)}
          exerciseName={progressionModal.exerciseName}
          previousBest={progressionModal.previousBest}
          onApplyProgression={handleProgressionChoice}
        />
      )}

      {/* Progression Scope Modal */}
      {progressionScopeModal && (
        <ProgressionScopeModal
          isOpen={progressionScopeModal.isOpen}
          onClose={() => setProgressionScopeModal(null)}
          progressionType={progressionScopeModal.progressionType}
          increment={progressionScopeModal.increment}
          onApplyToAll={applyProgressionToAll}
          onApplyToHighest={applyProgressionToHighest}
        />
      )}

      {/* Complete Workout Modal */}
      {showCompleteModal && (
        <div
          className="bg-opacity-75 fixed inset-0 z-[9999] flex items-center justify-center bg-black p-4"
          role="dialog"
          aria-modal="true"
          aria-labelledby="complete-workout-title"
          aria-describedby="complete-workout-desc"
          onClick={() => {
            restoreFocusInline();
            closeCompleteModal();
          }}
        >
          <FocusTrap
            onEscape={() => {
              restoreFocusInline();
              closeCompleteModal();
            }}
            preventScroll
          >
            <div
              className="w-full max-w-lg card p-6 shadow-2xl"
              onClick={(e) => e.stopPropagation()}
            >
              <h3 id="complete-workout-title" className="mb-1 text-xl font-bold text-purple-700 dark:text-purple-300">
                Complete Workout
              </h3>
              <p id="complete-workout-desc" className="mb-4 text-sm text-secondary">
                Review your performance compared to your previous best for each exercise.
              </p>
              <div className="max-h-[50vh] overflow-y-auto pr-1">
                <div className="space-y-3">
                  {exercises.map((ex, idx) => {
                    const curr = computeCurrentBest(ex);
                    const prev = previousExerciseData.get(ex.exerciseName)?.best;

                    // helpers to compare
                    const currWeight = curr.bestWeight?.weight ?? 0;
                    const prevWeight = prev?.weight ?? 0;
                    const weightDelta = currWeight - prevWeight;
                    const weightBadge =
                      curr.bestWeight && prev
                        ? weightDelta > 0
                          ? "text-green-300"
                          : weightDelta < 0
                          ? "text-red-300"
                          : "text-gray-300"
                        : "text-gray-300";

                    const currVol = curr.bestVolume?.volume ?? 0;
                    const prevVol = (prev?.weight ?? 0) * (prev?.reps ?? 0);
                    const volDelta = currVol - prevVol;
                    const volBadge =
                      currVol && prev
                        ? volDelta > 0
                          ? "text-green-300"
                          : volDelta < 0
                          ? "text-red-300"
                          : "text-gray-300"
                        : "text-gray-300";

                    const fmtSet = (w?: number, r?: number, u?: "kg" | "lbs") =>
                      w ? `${w}${u ?? "kg"}${r ? ` × ${r}` : ""}` : "N/A";

                    return (
                      <div key={`${ex.exerciseName}-${idx}`} className="card p-3">
                        <div className="mb-2 text-sm font-semibold">{ex.exerciseName}</div>
                        <div className="grid grid-cols-2 gap-3 text-sm">
                          <div>
                            <div className="text-xs text-muted mb-1">Previous Best</div>
                            <div>Weight: {fmtSet(prev?.weight, prev?.reps, prev?.unit)}</div>
                            <div>Volume: {prev ? (prev.weight ?? 0) * (prev.reps ?? 0) : "N/A"}</div>
                          </div>
                          <div>
                            <div className="text-xs text-muted mb-1">Current Best</div>
                            <div className={weightBadge}>
                              Weight: {fmtSet(curr.bestWeight?.weight, curr.bestWeight?.reps, curr.bestWeight?.unit)}
                              {prev && curr.bestWeight?.weight !== undefined ? (
                                <span className="ml-2 text-xs opacity-80">
                                  {weightDelta > 0 ? `(+${weightDelta})` : weightDelta < 0 ? `(${weightDelta})` : "(=)"}
                                </span>
                              ) : null}
                            </div>
                            <div className={volBadge}>
                              Volume: {curr.bestVolume?.volume ?? "N/A"}
                              {prev ? (
                                <span className="ml-2 text-xs opacity-80">
                                  {volDelta > 0 ? `(+${volDelta})` : volDelta < 0 ? `(${volDelta})` : "(=)"}
                                </span>
                              ) : null}
                            </div>
                          </div>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
              <div className="mt-5 flex items-center gap-3">
                <button
                  onClick={async () => {
                    closeCompleteModal();
                    await handleSave();
                    // Navigate to history immediately after existing save flow handles navigation/notifications
                  }}
                  className="btn-primary flex-1 py-3 text-lg font-semibold"
                >
                  Complete Workout
                </button>
                <button
                  onClick={closeCompleteModal}
                  className="flex-1 rounded-lg bg-gray-200 py-3 text-lg font-medium transition-colors hover:bg-gray-300 text-gray-900 dark:bg-gray-700 dark:text-white dark:hover:bg-gray-600"
                >
                  Continue Workout
                </button>
              </div>
            </div>
          </FocusTrap>
        </div>
      )}
    </div>
  );
}

