"use client";

import React, { useMemo } from "react";
import { SetInput, type SetData } from "../set-input";
import { useUniversalDragReorder } from "~/hooks/use-universal-drag-reorder";

interface SetListProps {
  exerciseIndex: number;
  exerciseName: string;
  templateExerciseId?: number;
  sets: SetData[];
  readOnly: boolean;
  onUpdate: (exerciseIndex: number, setIndex: number, field: keyof SetData, value: string | number | undefined) => void;
  onToggleUnit: (exerciseIndex: number, setIndex: number) => void;
  onAddSet: (exerciseIndex: number) => void;
  onDeleteSet: (exerciseIndex: number, setIndex: number) => void;
}

export function SetList({
  exerciseIndex,
  exerciseName,
  templateExerciseId,
  sets,
  readOnly,
  onUpdate,
  onToggleUnit,
  onAddSet,
  onDeleteSet,
}: SetListProps) {
  // Build a stable display order for sets, preserving identity by set.id when available
  const displayOrder = useMemo(
    () =>
      sets.map((s, index) => ({
        set: s,
        originalIndex: index,
        identity: (s.id ?? `${exerciseIndex}-${index}`).toString(),
      })),
    [sets, exerciseIndex],
  );

  const [dragState, dragHandlers] = useUniversalDragReorder(
    displayOrder,
    (newDisplay) => {
      // Compute mapping from identity to original index
      const idToOriginal = new Map<string, number>();
      sets.forEach((s, i) => {
        idToOriginal.set((s.id ?? `${exerciseIndex}-${i}`).toString(), i);
      });

      // Build a list of moves [from -> to] based on the new display order
      // Ensure stable string identity for each target set
      const targetOrder: string[] = newDisplay.map((d) => {
        const id = d.set.id;
        if (id != null) return String(id);
        const original = idToOriginal.get((d.set.id as any)?.toString?.() ?? "");
        return `${exerciseIndex}-${(typeof original === "number" && original >= 0) ? original : d.originalIndex}`;
      });

      // Apply moves by swapping adjacent indices to avoid clobbering values.
      // We use field-level updates to match the parent's state model.
      const move = (from: number, to: number) => {
        if (from === to || from < 0 || to < 0) return;
        const step = from < to ? 1 : -1;
        for (let i = from; i !== to; i += step) {
          const a = i;
          const b = i + step;

          // swap fields between a and b
          const sa = sets[a];
          const sb = sets[b];
          if (!sa || !sb) continue;

          onUpdate(exerciseIndex, a, "weight", sb.weight);
          onUpdate(exerciseIndex, a, "reps", (sb.reps ?? undefined) as any);
          onUpdate(exerciseIndex, a, "sets", ((sb.sets ?? 1) as any));
          onUpdate(exerciseIndex, a, "unit", (sb.unit as any));

          onUpdate(exerciseIndex, b, "weight", sa.weight);
          onUpdate(exerciseIndex, b, "reps", (sa.reps ?? undefined) as any);
          onUpdate(exerciseIndex, b, "sets", ((sa.sets ?? 1) as any));
          onUpdate(exerciseIndex, b, "unit", (sa.unit as any));
        }
      };

      // Determine current identity order
      const currentIds = sets.map((s, i) => (s.id != null ? String(s.id) : `${exerciseIndex}-${i}`));
      const desiredIds: string[] = newDisplay.map((d) =>
        d.set.id != null ? String(d.set.id) : `${exerciseIndex}-${d.originalIndex}`
      );

      // Execute minimal swaps to transform currentIds -> desiredIds
      const cur = currentIds.slice();
      for (let to = 0; to < desiredIds.length; to++) {
        const want = desiredIds[to]!;
        const from = cur.indexOf(want);
        if (from === -1 || from === to) continue;
        move(from, to);
        const moved = cur.splice(from, 1)[0]!;
        cur.splice(to, 0, moved);
      }
    },
    // When dragging starts, nothing special yet for sets
  );

  return (
    <div className="space-y-2">
      {/* Render each set with drag affordance and delegation to universal drag handlers */}
      {displayOrder.map((item, displayIndex) => (
        <div
          key={item.identity}
          className={`rounded-md ${dragState.isDragging && dragState.draggedIndex === displayIndex ? "shadow-lg" : ""}`}
          onPointerDown={dragHandlers.onPointerDown(displayIndex)}
          // Prevent browser gestures on the container while dragging
          style={{ touchAction: "none" }}
          // Provide an element ref map if needed for precise drop targeting
          ref={(el) => dragHandlers.setCardElement?.(displayIndex, el as HTMLElement | null)}
        >
          <SetInput
            set={item.set}
            setIndex={displayIndex}
            exerciseIndex={exerciseIndex}
            exerciseName={exerciseName}
            templateExerciseId={templateExerciseId}
            onUpdate={onUpdate}
            onToggleUnit={onToggleUnit}
            onDelete={onDeleteSet}
            readOnly={readOnly}
            showDelete={sets.length > 1}
            onPointerDownForSet={dragHandlers.onPointerDown(displayIndex)}
          />
        </div>
      ))}

      {/* Add Set Button */}
      {!readOnly && (
        <div>
          <button
            className="w-full py-2 text-sm rounded-md border border-gray-200 bg-white text-gray-900 hover:bg-gray-100 transition-colors dark:border-gray-600 dark:bg-gray-700 dark:text-white dark:hover:bg-gray-600"
            onClick={(e) => {
              // Prevent any parent pointer/mouse handlers from receiving this click,
              // which can cause duplicate addSet invocations.
              e.preventDefault();
              e.stopPropagation();
              // Some browsers fire both pointer and click in quick succession in nested handlers.
              // Use a per-event guard flag to avoid double-fire.
              const ne = (e as unknown as { nativeEvent?: Record<string, unknown> }).nativeEvent;
              if (ne?._addSetHandled) return;
              if (ne) ne._addSetHandled = true;
              onAddSet(exerciseIndex);
            }}
            onPointerDown={(e) => e.stopPropagation()}
            onMouseDown={(e) => e.stopPropagation()}
            onTouchStart={(e) => e.stopPropagation()}
          >
            Add Set
          </button>
        </div>
      )}
    </div>
  );
}

export default SetList;

import type { NextRequest } from "next/server";
import { NextResponse } from "next/server";
import { currentUser } from "@clerk/nextjs/server";
import type * as oauth from "oauth4webapi";
import { db } from "~/server/db";
import { userIntegrations } from "~/server/db/schema";
import { eq, and } from "drizzle-orm";
import { env } from "~/env";

export async function GET(request: NextRequest) {
  try {
    const user = await currentUser();
    if (!user) {
      return NextResponse.redirect(`${request.nextUrl.origin}/connect-whoop?error=unauthorized`);
    }

    const { searchParams } = request.nextUrl;
    const error = searchParams.get("error");

    // Check for OAuth error
    if (error) {
      return NextResponse.redirect(`${request.nextUrl.origin}/connect-whoop?error=${error}`);
    }

    // Verify state parameter
    const state = searchParams.get("state");
    const storedState = request.cookies.get("whoop_oauth_state")?.value;
    if (!state || !storedState || state !== storedState) {
      return NextResponse.redirect(`${request.nextUrl.origin}/connect-whoop?error=invalid_state`);
    }

    // Exchange authorization code for access token
    const authorizationServer: oauth.AuthorizationServer = {
      issuer: "https://api.prod.whoop.com",
      authorization_endpoint: "https://api.prod.whoop.com/oauth/oauth2/auth",
      token_endpoint: "https://api.prod.whoop.com/oauth/oauth2/token",
    };

    const client: oauth.Client = {
      client_id: env.WHOOP_CLIENT_ID!,
      client_secret: env.WHOOP_CLIENT_SECRET!,
    };

    const redirectUri = `${request.nextUrl.origin}/api/auth/whoop/callback`;

    // Get the authorization code (we already validated state)
    const code = searchParams.get("code");
    if (!code) {
      return NextResponse.redirect(`${request.nextUrl.origin}/connect-whoop?error=no_code`);
    }

    console.log("Token exchange attempt:", {
      redirectUri,
      clientId: env.WHOOP_CLIENT_ID!.substring(0, 8) + "...",
    });

    // Make token exchange request directly to avoid oauth4webapi validation issues
    const tokenRequest = {
      grant_type: "authorization_code",
      code: code,
      redirect_uri: redirectUri,
      client_id: env.WHOOP_CLIENT_ID!,
      client_secret: env.WHOOP_CLIENT_SECRET!,
    };

    const tokenResponse = await fetch(authorizationServer.token_endpoint!, {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
        "Accept": "application/json",
      },
      body: new URLSearchParams(tokenRequest).toString(),
    });

    console.log("Token response status:", tokenResponse.status);
    if (!tokenResponse.ok) {
      const errorText = await tokenResponse.text();
      console.error("Token exchange error response:", errorText);
      throw new Error(`Token exchange failed: ${tokenResponse.status} - ${errorText}`);
    }

    const tokens: unknown = await tokenResponse.json();

    if (typeof tokens !== "object" || tokens === null) {
      throw new Error("Token response was not an object");
    }

    const tok = tokens as {
      access_token?: string;
      refresh_token?: string | null;
      expires_in?: number;
      scope?: string;
    };

    console.log("Tokens received:", {
      hasAccessToken: !!tok.access_token,
      hasRefreshToken: !!tok.refresh_token,
      expiresIn: tok.expires_in,
      scope: tok.scope,
    });

    // Calculate expires_at
    const expiresAt = tok.expires_in 
      ? new Date(Date.now() + tok.expires_in * 1000)
      : null;

    // Store tokens in database (upsert pattern)
    const existingIntegration = await db
      .select()
      .from(userIntegrations)
      .where(
        and(
          eq(userIntegrations.user_id, user.id),
          eq(userIntegrations.provider, "whoop")
        )
      );

    if (existingIntegration.length > 0) {
      await db
        .update(userIntegrations)
        .set({
          accessToken: tok.access_token!,
          refreshToken: tok.refresh_token ?? null,
          expiresAt,
          scope: tok.scope ?? "read:workout offline",
          isActive: true,
          updatedAt: new Date(),
        })
        .where(
          and(
            eq(userIntegrations.user_id, user.id),
            eq(userIntegrations.provider, "whoop")
          )
        );
    } else {
      await db.insert(userIntegrations).values({
        user_id: user.id,
        provider: "whoop",
        accessToken: tok.access_token!,
        refreshToken: tok.refresh_token ?? null,
        expiresAt,
        scope: tok.scope ?? "read:workout offline",
        isActive: true,
      });
    }

    // Clear state cookie and redirect to success page
    const response = NextResponse.redirect(`${request.nextUrl.origin}/connect-whoop?success=true`);
    response.cookies.delete("whoop_oauth_state");

    return response;
  } catch (error) {
    console.error("Whoop OAuth callback error:", error);
    return NextResponse.redirect(`${request.nextUrl.origin}/connect-whoop?error=token_exchange_failed`);
  }
}

import type { NextRequest } from "next/server";
import { NextResponse } from "next/server";
import { currentUser } from "@clerk/nextjs/server";
import type * as oauth from "oauth4webapi";
import { db } from "~/server/db";
import { userIntegrations, externalWorkoutsWhoop } from "~/server/db/schema";
import { eq, and } from "drizzle-orm";
import { env } from "~/env";
import { checkRateLimit } from "~/lib/rate-limit";

interface WhoopWorkout {
  id: string;
  start: string;
  end: string;
  timezone_offset: string;
  sport_name: string;
  score_state: string;
  score?: unknown;
  during?: unknown;
  zone_duration?: unknown;
}

type IntegrationRecord = {
  id: number;
  accessToken: string;
  refreshToken: string | null;
  expiresAt: string | Date | null;
};

async function refreshTokenIfNeeded(integration: IntegrationRecord) {
  // Check if token is expired or will expire in next 5 minutes
  const expiryBuffer = 5 * 60 * 1000; // 5 minutes in milliseconds
  const now = new Date();

  if (integration.expiresAt && new Date(integration.expiresAt).getTime() - now.getTime() < expiryBuffer) {
    if (!integration.refreshToken) {
      throw new Error("Access token expired and no refresh token available");
    }

    const authorizationServer: oauth.AuthorizationServer = {
      issuer: "https://api.prod.whoop.com",
      authorization_endpoint: "https://api.prod.whoop.com/oauth/oauth2/auth",
      token_endpoint: "https://api.prod.whoop.com/oauth/oauth2/token",
    };

    const client: oauth.Client = {
      client_id: env.WHOOP_CLIENT_ID!,
      client_secret: env.WHOOP_CLIENT_SECRET!,
    };

    // Make refresh token request directly
    const refreshRequest = {
      grant_type: "refresh_token",
      refresh_token: integration.refreshToken,
      client_id: env.WHOOP_CLIENT_ID!,
      client_secret: env.WHOOP_CLIENT_SECRET!,
    };

    const tokenResponse = await fetch(authorizationServer.token_endpoint!, {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
        "Accept": "application/json",
      },
      body: new URLSearchParams(refreshRequest).toString(),
    });

    if (!tokenResponse.ok) {
      const errorText = await tokenResponse.text();
      throw new Error(`Token refresh failed: ${tokenResponse.status} - ${errorText}`);
    }

    const tokens: unknown = await tokenResponse.json();

    if (typeof tokens !== "object" || tokens === null) {
      throw new Error("Unexpected token response shape");
    }
    const t = tokens as {
      access_token?: string;
      refresh_token?: string | null;
      expires_in?: number;
    };

    const expiresAt = t.expires_in 
      ? new Date(Date.now() + t.expires_in * 1000)
      : null;

    // Update tokens in database
    await db
      .update(userIntegrations)
      .set({
        accessToken: t.access_token!,
        refreshToken: t.refresh_token ?? integration.refreshToken,
        expiresAt,
        updatedAt: new Date(),
      })
      .where(eq(userIntegrations.id, Number(integration.id)));

    return t.access_token!;
  }

  return integration.accessToken;
}

export async function POST(request: NextRequest) {
  try {
    const user = await currentUser();
    if (!user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    // Check rate limit (use shared env presets)
    const rateLimit = await checkRateLimit(
      user.id,
      "whoop_sync",
      env.WHOOP_SYNC_RATE_LIMIT_PER_HOUR,
      60 * 60 * 1000 // 1 hour
    );

    if (!rateLimit.allowed) {
      return NextResponse.json(
        {
          error: "Rate limit exceeded",
          message: `You can only sync ${env.WHOOP_SYNC_RATE_LIMIT_PER_HOUR} times per hour. Try again in ${Math.ceil(rateLimit.retryAfter! / 60)} minutes.`,
          retryAfter: rateLimit.retryAfter,
          resetTime: rateLimit.resetTime.toISOString(),
        },
        { 
          status: 429,
          headers: {
            "Retry-After": rateLimit.retryAfter!.toString(),
            "X-RateLimit-Limit": env.WHOOP_SYNC_RATE_LIMIT_PER_HOUR.toString(),
            "X-RateLimit-Remaining": rateLimit.remaining.toString(),
            "X-RateLimit-Reset": Math.floor(rateLimit.resetTime.getTime() / 1000).toString(),
          }
        }
      );
    }

    // Get user's Whoop integration
    const [integration] = await db
      .select()
      .from(userIntegrations)
      .where(
        and(
          eq(userIntegrations.user_id, user.id),
          eq(userIntegrations.provider, "whoop"),
          eq(userIntegrations.isActive, true)
        )
      );

    if (!integration) {
      return NextResponse.json({ error: "Whoop integration not found" }, { status: 404 });
    }

    // Refresh token if needed
    const accessToken = await refreshTokenIfNeeded(integration);

    // Fetch workouts from Whoop API (v2 endpoint) with pagination
    // Try to get more historical workouts by adding limit parameter
    const whoopResponse = await fetch("https://api.prod.whoop.com/developer/v2/activity/workout?limit=25", {
      headers: {
        Authorization: `Bearer ${accessToken}`,
        "Content-Type": "application/json",
      },
    });

    if (!whoopResponse.ok) {
      const errorText = await whoopResponse.text();
      console.error("Whoop API error:", whoopResponse.status, errorText);
      return NextResponse.json(
        { error: `Whoop API error: ${whoopResponse.status} - ${errorText}` },
        { status: whoopResponse.status }
      );
    }

    const whoopData: unknown = await whoopResponse.json();
    if (typeof whoopData !== "object" || whoopData === null) {
      return NextResponse.json(
        { error: "Unexpected Whoop API response shape" },
        { status: 502 },
      );
    }

    // Narrow shape
    const dataArr = (whoopData as { data?: unknown }).data;
    const recordsArr = (whoopData as { records?: unknown }).records;

    console.log("Whoop API response structure:", {
      keys: Object.keys(whoopData as Record<string, unknown>),
      recordsLength: Array.isArray(recordsArr) ? recordsArr.length : undefined,
      dataLength: Array.isArray(dataArr) ? dataArr.length : undefined,
    });

    // v2 API might use 'data' instead of 'records'
    const workoutsRaw = (Array.isArray(dataArr) ? dataArr : Array.isArray(recordsArr) ? recordsArr : []) as unknown[];

    const workouts: WhoopWorkout[] = workoutsRaw.map((w) => {
      const o = w as Record<string, unknown>;
      return {
        id: String(o.id ?? ""),
        start: String(o.start ?? ""),
        end: String(o.end ?? ""),
        timezone_offset: String(o.timezone_offset ?? ""),
        sport_name: String(o.sport_name ?? ""),
        score_state: String(o.score_state ?? ""),
        score: o.score,
        during: o.during,
        zone_duration: o.zone_duration,
      };
    });

    let newWorkouts = 0;
    let duplicates = 0;

    // Process each workout
    for (const workout of workouts) {
      try {
        // Check if workout already exists
        const [existingWorkout] = await db
          .select()
          .from(externalWorkoutsWhoop)
          .where(eq(externalWorkoutsWhoop.whoopWorkoutId, workout.id));

        if (existingWorkout) {
          duplicates++;
          continue;
        }

        // Insert new workout
        await db.insert(externalWorkoutsWhoop).values({
          user_id: user.id,
          whoopWorkoutId: workout.id,
          start: new Date(workout.start),
          end: new Date(workout.end),
          timezone_offset: workout.timezone_offset,
          sport_name: workout.sport_name,
          score_state: workout.score_state,
          score: workout.score ?? null,
          during: workout.during ?? null,
          zone_duration: workout.zone_duration ?? null,
        });

        newWorkouts++;
      } catch (error) {
        console.error(`Error processing workout ${workout.id}:`, error);
      }
    }

    return NextResponse.json(
      {
        success: true,
        totalWorkouts: workouts.length,
        newWorkouts,
        duplicates,
        rateLimit: {
          remaining: rateLimit.remaining,
          resetTime: rateLimit.resetTime.toISOString(),
        },
      },
      {
        headers: {
          "X-RateLimit-Limit": env.WHOOP_SYNC_RATE_LIMIT_PER_HOUR.toString(),
          "X-RateLimit-Remaining": rateLimit.remaining.toString(),
          "X-RateLimit-Reset": Math.floor(rateLimit.resetTime.getTime() / 1000).toString(),
        }
      }
    );
  } catch (error) {
    console.error("Sync workouts error:", error);
    return NextResponse.json(
      { error: error instanceof Error ? error.message : "Failed to sync workouts" },
      { status: 500 }
    );
  }
}

