interface StartWorkoutPageProps {
  searchParams: Promise<{ templateId?: string }>;
}

export default async function StartWorkoutPage({
  searchParams,
}: StartWorkoutPageProps) {
  const user = await currentUser();
  const { templateId } = await searchParams;

  if (!user) {
    redirect("/sign-in");
  }

  // SSR prefetch + hydrate to speed up template loading and recent list
  const qc = getQueryClient();
  await prefetchWorkoutStart(qc);

  // If templateId is provided, prefetch that template as well
  if (templateId) {
    const id = parseInt(templateId);
    if (!isNaN(id)) {
      void api.templates.getById.prefetch({ id });
    }
  }

  const state = getDehydratedState(qc);

  return (
    <ClientHydrate state={state}>
      <main className="min-h-screen">
        <div className="container mx-auto px-4 py-6">
          {/* Header */}
          <div className="mb-6 flex items-center gap-4">
            <Link href="/" className="text-purple-400 hover:text-purple-300">
              ← Back
            </Link>
            <h1 className="text-2xl font-bold">Start Workout</h1>
          </div>

          {/* Workout Starter */}
          <WorkoutStarter
            initialTemplateId={templateId ? parseInt(templateId) : undefined}
          />
        </div>
      </main>
    </ClientHydrate>
  );
}

"use client";

import { useState, useRef, useCallback } from "react";

export interface DragReorderState {
  draggedIndex: number | null;
  dragOverIndex: number | null;
  isDragging: boolean;
  dragOffset: { x: number; y: number };
}

export interface DragReorderHandlers {
  onDragStart: (index: number) => (e: React.DragEvent) => void;
  onDragEnd: (e: React.DragEvent) => void;
  onDragOver: (index: number) => (e: React.DragEvent) => void;
  onDrop: (index: number) => (e: React.DragEvent) => void;
  onDragEnter: (index: number) => (e: React.DragEvent) => void;
  onDragLeave: (e: React.DragEvent) => void;
}

export function useDragReorder<T>(
  items: T[],
  onReorder: (newItems: T[]) => void,
  onStartDrag?: (index: number) => void,
  onEndDrag?: () => void
): [DragReorderState, DragReorderHandlers] {
  const [draggedIndex, setDraggedIndex] = useState<number | null>(null);
  const [dragOverIndex, setDragOverIndex] = useState<number | null>(null);
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });

  const dragStartPosRef = useRef<{ x: number; y: number }>({ x: 0, y: 0 });

  const onDragStart = useCallback(
    (index: number) => (e: React.DragEvent) => {
      setDraggedIndex(index);
      setIsDragging(true);
      dragStartPosRef.current = { x: e.clientX, y: e.clientY };

      // Set drag image to be invisible so we can control the visual feedback
      const img = new Image();
      img.src = 'data:image/gif;base64,R0lGODlhAQABAIAAAAUEBAAAACwAAAAAAQABAAACAkQBADs=';
      e.dataTransfer.setDragImage(img, 0, 0);

      // Set the data for the drag operation
      e.dataTransfer.setData('text/plain', index.toString());
      e.dataTransfer.effectAllowed = 'move';

      onStartDrag?.(index);
    },
    [onStartDrag]
  );

  const onDragEnd = useCallback(
    (e: React.DragEvent) => {
      setDraggedIndex(null);
      setDragOverIndex(null);
      setIsDragging(false);
      setDragOffset({ x: 0, y: 0 });
      onEndDrag?.();
    },
    [onEndDrag]
  );

  const onDragOver = useCallback(
    (index: number) => (e: React.DragEvent) => {
      e.preventDefault();
      e.dataTransfer.dropEffect = 'move';

      // Update drag offset for visual feedback
      const deltaX = e.clientX - dragStartPosRef.current.x;
      const deltaY = e.clientY - dragStartPosRef.current.y;
      setDragOffset({ x: deltaX, y: deltaY });

      if (draggedIndex !== null && index !== draggedIndex) {
        setDragOverIndex(index);
      }
    },
    [draggedIndex]
  );

  const onDrop = useCallback(
    (index: number) => (e: React.DragEvent) => {
      e.preventDefault();

      if (draggedIndex !== null && draggedIndex !== index) {
        const newItems = [...items];
        const draggedItem = newItems[draggedIndex];

        if (draggedItem) {
          // Remove the dragged item
          newItems.splice(draggedIndex, 1);

          // Insert at the new position
          const targetIndex = draggedIndex < index ? index - 1 : index;
          newItems.splice(targetIndex, 0, draggedItem);

          onReorder(newItems);
        }
      }

      setDraggedIndex(null);
      setDragOverIndex(null);
      setIsDragging(false);
      setDragOffset({ x: 0, y: 0 });
    },
    [draggedIndex, items, onReorder]
  );

  const onDragEnter = useCallback(
    (index: number) => (e: React.DragEvent) => {
      e.preventDefault();
      if (draggedIndex !== null && index !== draggedIndex) {
        setDragOverIndex(index);
      }
    },
    [draggedIndex]
  );

  const onDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    // Only clear drag over index if we're really leaving the drop zone
    // (not just moving to a child element)
    const rect = (e.currentTarget as HTMLElement).getBoundingClientRect();
    const x = e.clientX;
    const y = e.clientY;

    if (x < rect.left || x > rect.right || y < rect.top || y > rect.bottom) {
      setDragOverIndex(null);
    }
  }, []);

  const state: DragReorderState = {
    draggedIndex,
    dragOverIndex,
    isDragging,
    dragOffset,
  };

  const handlers: DragReorderHandlers = {
    onDragStart,
    onDragEnd,
    onDragOver,
    onDrop,
    onDragEnter,
    onDragLeave,
  };

  return [state, handlers];
}

"use client";

import { useCallback, useEffect, useMemo, useRef, useState } from "react";
import {
  enqueueWorkoutSave,
  getQueue,
  getQueueLength,
  dequeue,
  requeueFront,
  updateItem,
  pruneExhausted,
  type SaveWorkoutPayload,
} from "~/lib/offline-queue";
import { api } from "~/trpc/react";

type FlushStatus = "idle" | "flushing" | "error" | "done";

function sleep(ms: number) {
  return new Promise((r) => setTimeout(r, ms));
}

function backoff(attempt: number) {
  // 500ms * 2^attempt, capped at 8s
  return Math.min(500 * Math.pow(2, attempt), 8000);
}

export function useOfflineSaveQueue() {
  const [queueSize, setQueueSize] = useState<number>(() =>
    typeof window !== "undefined" ? getQueueLength() : 0,
  );
  const [status, setStatus] = useState<FlushStatus>("idle");
  const [lastError, setLastError] = useState<string | null>(null);
  const isFlushingRef = useRef(false);

  const utils = api.useUtils();
  const saveWorkout = api.workouts.save.useMutation();

  const refreshCount = useCallback(() => {
    if (typeof window === "undefined") return;
    setQueueSize(getQueueLength());
  }, []);

  const enqueue = useCallback((payload: SaveWorkoutPayload) => {
    const id = enqueueWorkoutSave(payload);
    setQueueSize((n) => n + 1);
    return id;
  }, []);

  const flush = useCallback(async () => {
    if (isFlushingRef.current) return;
    isFlushingRef.current = true;
    setStatus("flushing");
    setLastError(null);

    try {
      pruneExhausted();

      // Process FIFO
      // We will drain the queue in this session unless errors request a retry
      while (true) {
        const item = dequeue();
        if (!item) break;

        if (item.type !== "workout_save") {
          // Unknown item; skip
          continue;
        }

        try {
          // Attempt to save
          await saveWorkout.mutateAsync({
            sessionId: item.payload.sessionId,
            exercises: item.payload.exercises,
          });

          // On success, invalidate recent workouts etc.
          await utils.workouts.getRecent.invalidate();

          // Reduce count
          setQueueSize((n) => Math.max(0, n - 1));
        } catch (err) {
          const message =
            err && typeof err === "object" && "message" in err
              ? String((err as Error).message)
              : "Network or server error";

          // Network-ish failures: requeue with backoff
          const attempts = item.attempts + 1;
          updateItem(item.id, { attempts, lastError: message });

          if (attempts >= 8) {
            // Give up on this item for now; keep it in storage but don't requeue to front
            setLastError(
              "Some items failed to sync after multiple attempts. See Sync Indicator for details.",
            );
          } else {
            // Wait with exponential backoff then requeue at front for immediate retry later
            await sleep(backoff(item.attempts));
            requeueFront({ ...item, attempts });
          }

          // Exit loop for now to avoid tight retry loops; let user trigger flush again
          break;
        }
      }

      setStatus("done");
    } catch (e) {
      setStatus("error");
      setLastError("Unexpected error while flushing queue.");
    } finally {
      isFlushingRef.current = false;
      // Small debounce before returning to idle
      setTimeout(() => setStatus("idle"), 500);
    }
  }, [saveWorkout, utils.workouts.getRecent]);

  // Auto-flush when coming back online
  useEffect(() => {
    const onOnline = () => {
      if (getQueueLength() > 0) {
        void flush();
      }
    };
    const onStorage = (e: StorageEvent) => {
      if (e.key === "offline.queue.v1") {
        setQueueSize(getQueueLength());
      }
    };

    window.addEventListener("online", onOnline);
    window.addEventListener("storage", onStorage);
    return () => {
      window.removeEventListener("online", onOnline);
      window.removeEventListener("storage", onStorage);
    };
  }, [flush]);

  const items = useMemo(() => {
    if (typeof window === "undefined") return [];
    return getQueue();
  }, [queueSize]);

  return {
    queueSize,
    status,
    lastError,
    items,
    enqueue,
    flush,
    refreshCount,
    isFlushing: status === "flushing",
  };
}

"use client";

import { useState, useRef, useCallback, useEffect } from "react";

export interface SwipeSettings {
  dismissThreshold: number; // Distance in pixels to trigger dismiss
  velocityThreshold: number; // Velocity threshold for dismiss
  friction: number; // Momentum decay factor (0-1)
  minimumVelocity: number; // Minimum velocity to start momentum
  framesPerSecond: number; // For velocity calculation
}

export const DEFAULT_SWIPE_SETTINGS: SwipeSettings = {
  dismissThreshold: 150,
  velocityThreshold: 8,
  friction: 0.95,
  minimumVelocity: 0.5,
  framesPerSecond: 60,
};

export interface SwipeGestureState {
  translateX: number;
  translateY: number;
  isDragging: boolean;
  velocity: number;
  isDismissed: boolean;
}

export interface SwipeGestureHandlers {
  onTouchStart: (e: React.TouchEvent) => void;
  onTouchMove: (e: React.TouchEvent) => void;
  onTouchEnd: () => void;
  onMouseDown: (e: React.MouseEvent) => void;
  onMouseMove: (e: React.MouseEvent) => void;
  onMouseUp: () => void;
  onMouseLeave: () => void;
}

export function useSwipeGestures(
  onDismiss?: () => void,
  settings: Partial<SwipeSettings> = {},
  direction: "horizontal" | "vertical" = "horizontal"
): [SwipeGestureState, SwipeGestureHandlers, () => void] {
  const config = { ...DEFAULT_SWIPE_SETTINGS, ...settings };

  const [translateX, setTranslateX] = useState(0);
  const [translateY, setTranslateY] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [velocity, setVelocity] = useState(0);
  const [isDismissed, setIsDismissed] = useState(false);

  const touchStartXRef = useRef<number | null>(null);
  const touchStartYRef = useRef<number | null>(null);
  const lastTouchXRef = useRef<number>(0);
  const lastTouchYRef = useRef<number>(0);
  const lastTouchTimeRef = useRef<number>(0);
  const animationRef = useRef<number | null>(null);
  const isMouseRef = useRef(false);

  const startMomentumAnimation = useCallback(() => {
    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
    }

    const animate = () => {
      setVelocity((prevVelocity) => {
        const newVelocity = prevVelocity * config.friction;
        if (Math.abs(newVelocity) < 0.1) {
          // Animation finished - always snap back to center
          if (direction === "horizontal") {
            setTranslateX(0);
          } else {
            setTranslateY(0);
          }
          return 0;
        }

        if (direction === "horizontal") {
          setTranslateX((prevTranslateX) => {
            const newTranslateX = prevTranslateX + newVelocity;
            if (Math.abs(newTranslateX) > config.dismissThreshold || Math.abs(velocity) > config.velocityThreshold) {
              setIsDismissed(true);
              onDismiss?.();
              return 0; // Reset to center immediately
            }
            return newTranslateX;
          });
        } else {
          setTranslateY((prevTranslateY) => {
            const newTranslateY = prevTranslateY + newVelocity;
            if (Math.abs(newTranslateY) > config.dismissThreshold || Math.abs(velocity) > config.velocityThreshold) {
              setIsDismissed(true);
              onDismiss?.();
              return 0; // Reset to center immediately
            }
            return newTranslateY;
          });
        }

        animationRef.current = requestAnimationFrame(animate);
        return newVelocity;
      });
    };

    animationRef.current = requestAnimationFrame(animate);
  }, [config, direction, translateX, translateY, velocity, onDismiss]);

  const handleStart = useCallback((clientX: number, clientY: number, isMouse = false) => {
    touchStartXRef.current = clientX;
    touchStartYRef.current = clientY;
    lastTouchXRef.current = clientX;
    lastTouchYRef.current = clientY;
    lastTouchTimeRef.current = Date.now();
    setIsDragging(true);
    setVelocity(0);
    isMouseRef.current = isMouse;

    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
    }
  }, []);

  const handleMove = useCallback((clientX: number, clientY: number, preventDefault = true) => {
    if (!isDragging || !touchStartXRef.current || !touchStartYRef.current) return;

    if (preventDefault) {
      // Note: We can't call preventDefault here for mouse events, 
      // it should be called in the component's event handler
    }

    const deltaX = clientX - touchStartXRef.current;
    const deltaY = clientY - touchStartYRef.current;

    if (direction === "horizontal") {
      // Only track horizontal movement if it's more significant than vertical
      if (Math.abs(deltaX) > Math.abs(deltaY)) {
        setTranslateX(deltaX);

        // Calculate velocity for momentum
        const currentTime = Date.now();
        const timeDelta = currentTime - lastTouchTimeRef.current;
        const positionDelta = clientX - lastTouchXRef.current;

        if (timeDelta > 0) {
          setVelocity((positionDelta / timeDelta) * (1000 / config.framesPerSecond));
        }

        lastTouchXRef.current = clientX;
        lastTouchTimeRef.current = currentTime;
      }
    } else {
      // Vertical direction
      if (Math.abs(deltaY) > Math.abs(deltaX)) {
        setTranslateY(deltaY);

        const currentTime = Date.now();
        const timeDelta = currentTime - lastTouchTimeRef.current;
        const positionDelta = clientY - lastTouchYRef.current;

        if (timeDelta > 0) {
          setVelocity((positionDelta / timeDelta) * (1000 / config.framesPerSecond));
        }

        lastTouchYRef.current = clientY;
        lastTouchTimeRef.current = currentTime;
      }
    }
  }, [isDragging, direction, config.framesPerSecond]);

  const handleEnd = useCallback(() => {
    if (!isDragging) return;

    setIsDragging(false);
    isMouseRef.current = false;

    const currentTranslate = direction === "horizontal" ? translateX : translateY;

    // Check if should dismiss or animate back
    if (Math.abs(currentTranslate) > config.dismissThreshold || Math.abs(velocity) > config.velocityThreshold) {
      // Trigger dismiss callback but reset position immediately
      setIsDismissed(true);
      onDismiss?.();
      // Reset position so card doesn't stay offscreen
      if (direction === "horizontal") {
        setTranslateX(0);
      } else {
        setTranslateY(0);
      }
    } else if (Math.abs(velocity) > config.minimumVelocity) {
      // Start momentum animation
      startMomentumAnimation();
    } else {
      // Snap back to center
      if (direction === "horizontal") {
        setTranslateX(0);
      } else {
        setTranslateY(0);
      }
    }
  }, [isDragging, direction, translateX, translateY, velocity, config, onDismiss, startMomentumAnimation]);

  // Touch handlers
  const onTouchStart = useCallback((e: React.TouchEvent) => {
    const touch = e.touches[0];
    if (!touch) return;
    handleStart(touch.clientX, touch.clientY);
  }, [handleStart]);

  const onTouchMove = useCallback((e: React.TouchEvent) => {
    // Don't call preventDefault() to avoid passive event listener warning
    // The CSS touch-action property handles scroll prevention
    const touch = e.touches[0];
    if (!touch) return;
    handleMove(touch.clientX, touch.clientY, false);
  }, [handleMove]);

  const onTouchEnd = useCallback(() => {
    handleEnd();
  }, [handleEnd]);

  // Mouse handlers for desktop support
  const onMouseDown = useCallback((e: React.MouseEvent) => {
    e.preventDefault();
    handleStart(e.clientX, e.clientY, true);
  }, [handleStart]);

  const onMouseMove = useCallback((e: React.MouseEvent) => {
    if (!isMouseRef.current) return;
    handleMove(e.clientX, e.clientY);
  }, [handleMove]);

  const onMouseUp = useCallback(() => {
    if (!isMouseRef.current) return;
    handleEnd();
  }, [handleEnd]);

  const onMouseLeave = useCallback(() => {
    if (!isMouseRef.current) return;
    handleEnd();
  }, [handleEnd]);

  const reset = useCallback(() => {
    setTranslateX(0);
    setTranslateY(0);
    setIsDragging(false);
    setVelocity(0);
    setIsDismissed(false);
    isMouseRef.current = false;
    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
    }
  }, []);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, []);

  const state: SwipeGestureState = {
    translateX,
    translateY,
    isDragging,
    velocity,
    isDismissed,
  };

  const handlers: SwipeGestureHandlers = {
    onTouchStart,
    onTouchMove,
    onTouchEnd,
    onMouseDown,
    onMouseMove,
    onMouseUp,
    onMouseLeave,
  };

  return [state, handlers, reset];
}

"use client";

import { useState, useRef, useCallback, useEffect } from "react";

export interface UniversalDragState {
  draggedIndex: number | null;
  dragOverIndex: number | null;
  isDragging: boolean;
  dragOffset: { x: number; y: number };
  dragStartPos: { x: number; y: number };
}

export interface UniversalDragHandlers {
  onPointerDown: (index: number, opts?: { force?: boolean }) => (e: React.PointerEvent | React.MouseEvent | React.TouchEvent) => void;
  onPointerMove: (e: React.PointerEvent | React.MouseEvent | React.TouchEvent) => void;
  onPointerUp: (e: React.PointerEvent | React.MouseEvent | React.TouchEvent) => void;
  setCardElement: (index: number, element: HTMLElement | null) => void;
}

export function useUniversalDragReorder<T>(
  items: T[],
  onReorder: (newItems: T[]) => void,
  onStartDrag?: (index: number) => void,
  onEndDrag?: () => void
): [UniversalDragState, UniversalDragHandlers] {
  const [draggedIndex, setDraggedIndex] = useState<number | null>(null);
  const [dragOverIndex, setDragOverIndex] = useState<number | null>(null);
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [dragStartPos, setDragStartPos] = useState({ x: 0, y: 0 });

  // Gesture tuning constants (mobile-first)
  const DRAG_START_SLOP = 14; // require more movement before drag starts
  const AXIS_LOCK_Y_RATIO = 0.75; // vertical must dominate to start drag
  const REORDER_CROSS_TOLERANCE_PX = 14; // extra tolerance before switching targets
  const AUTOSCROLL_ZONE = 50; // px from viewport edge to start auto-scroll
  const AUTOSCROLL_MAX_PX_PER_FRAME = 6; // slow, controlled auto-scroll

  const hasDragStarted = useRef(false);
  const dragStartTime = useRef(0);
  const axisLockedToY = useRef(false);
  const lastInsertionIndexRef = useRef<number | null>(null);
  const cardElements = useRef<(HTMLElement | null)[]>([]);
  const animationFrameRef = useRef<number | undefined>(undefined);
  const autoScrollRef = useRef<number | undefined>(undefined);
  const scrollSpeed = useRef(0);
  const initialScrollY = useRef(0);
  const currentScrollY = useRef(0);

  // Get pointer position from different event types
  const getPointerPos = (
    e: React.PointerEvent | React.MouseEvent | React.TouchEvent | PointerEvent | MouseEvent | TouchEvent,
  ): { x: number; y: number } => {
    // TouchEvent path
    if ("touches" in e) {
      const t = (e).touches;
      if (t && t.length > 0) {
        return { x: t[0]!.clientX, y: t[0]!.clientY };
      }
    }
    // Pointer/Mouse path
    if ("clientX" in e && "clientY" in e) {
      const p = e;
      return { x: p.clientX, y: p.clientY };
    }
    return { x: 0, y: 0 };
  };

  // Find the insertion point based on pointer position
  const findDropTarget = useCallback((x: number, y: number, excludeIndex?: number) => {
    let insertionIndex = 0;
    let closestDistance = Infinity;
    let closestIndex = 0;

    for (let i = 0; i < cardElements.current.length; i++) {
      const element = cardElements.current[i];
      if (!element || i === excludeIndex) continue;

      const rect = element.getBoundingClientRect();
      const centerY = rect.top + rect.height / 2;
      const distance = Math.abs(y - centerY);

      // Track the closest element for fallback
      if (x >= rect.left && x <= rect.right && distance < closestDistance) {
        closestDistance = distance;
        closestIndex = i;
      }

      // If the pointer is within horizontal bounds and above the center of this card
      if (x >= rect.left && x <= rect.right && y < centerY) {
        // We want to insert before this card
        return i;
      }

      // If we're past this card, the insertion point is after it
      if (x >= rect.left && x <= rect.right) {
        insertionIndex = i + 1;
      }
    }

    // If we're between elements or in a gap, use the closest element as reference
    if (closestDistance < Infinity) {
      const closestElement = cardElements.current[closestIndex];
      if (closestElement) {
        const rect = closestElement.getBoundingClientRect();
        const centerY = rect.top + rect.height / 2;
        // If we're below the closest element, insert after it
        return y >= centerY ? closestIndex + 1 : closestIndex;
      }
    }

    return insertionIndex;
  }, []);

  // Auto-scroll when dragging near viewport edges
  const handleAutoScroll = useCallback((clientY: number) => {
    const viewportHeight = window.innerHeight;

    let newScrollSpeed = 0;

    // Top zone
    if (clientY < AUTOSCROLL_ZONE) {
      const dist = AUTOSCROLL_ZONE - clientY;
      const ratio = Math.max(0, Math.min(1, dist / AUTOSCROLL_ZONE));
      // Ease-in curve for precision control
      const eased = Math.pow(ratio, 1.2);
      newScrollSpeed = -Math.max(0, Math.round(eased * AUTOSCROLL_MAX_PX_PER_FRAME));
    }
    // Bottom zone
    else if (clientY > viewportHeight - AUTOSCROLL_ZONE) {
      const dist = clientY - (viewportHeight - AUTOSCROLL_ZONE);
      const ratio = Math.max(0, Math.min(1, dist / AUTOSCROLL_ZONE));
      const eased = Math.pow(ratio, 1.2);
      newScrollSpeed = Math.max(0, Math.round(eased * AUTOSCROLL_MAX_PX_PER_FRAME));
    }

    scrollSpeed.current = newScrollSpeed;

    if (newScrollSpeed !== 0 && !autoScrollRef.current) {
      const scroll = () => {
        if (scrollSpeed.current !== 0) {
          window.scrollBy(0, scrollSpeed.current);
          currentScrollY.current = window.scrollY;
          autoScrollRef.current = requestAnimationFrame(scroll);
        } else {
          autoScrollRef.current = undefined;
        }
      };
      autoScrollRef.current = requestAnimationFrame(scroll);
    } else if (newScrollSpeed === 0 && autoScrollRef.current) {
      cancelAnimationFrame(autoScrollRef.current);
      autoScrollRef.current = undefined;
    }
  }, []);

  // Stop auto-scroll
  const stopAutoScroll = useCallback(() => {
    scrollSpeed.current = 0;
    if (autoScrollRef.current) {
      cancelAnimationFrame(autoScrollRef.current);
      autoScrollRef.current = undefined;
    }
  }, []);

  const onPointerDown = useCallback(
    (index: number, opts?: { force?: boolean }) => (e: React.PointerEvent | React.MouseEvent | React.TouchEvent) => {
      // Prevent dragging when clicking on interactive elements (except explicit drag handles)
      const target = e.target as HTMLElement;
      const isHandle = !!target.closest('[data-drag-handle="true"]');

      // IMPORTANT: Do NOT call preventDefault on React synthetic event here (React may mark as passive).
      // Instead, immediately add a non-passive native listener on the currentTarget to prevent default for the next move.
      const currentTarget = (e as React.SyntheticEvent).currentTarget as HTMLElement | null;

      if (currentTarget) {
        const nonPassiveMove = (ev: Event) => {
          try { ev.preventDefault(); } catch {}
        };
        // Add once to block the first move which typically starts scroll/text selection on touch
        currentTarget.addEventListener('touchmove', nonPassiveMove, { passive: false, once: true });
        currentTarget.addEventListener('pointermove', nonPassiveMove, { passive: false, once: true });
        currentTarget.addEventListener('mousemove', nonPassiveMove, { passive: false, once: true });
      }

      if (!isHandle && (target.closest('button') || target.closest('input') || target.closest('select'))) {
        return;
      }

      // iOS/Safari robustness: attempt to capture pointer if available
      const evt = e;
      const ct = (evt as React.SyntheticEvent).currentTarget as HTMLElement | null;
      // Only PointerEvent has pointerId
      if ("pointerId" in evt && ct && typeof (ct as HTMLElement & { setPointerCapture?: (pointerId: number) => void }).setPointerCapture === "function") {
        try {
          (ct as HTMLElement & { setPointerCapture: (pointerId: number) => void }).setPointerCapture(
            (evt).pointerId,
          );
        } catch {
          // ignore capture errors
        }
      }

      const pos = getPointerPos(e);
      setDragStartPos(pos);
      setDraggedIndex(index);
      dragStartTime.current = Date.now();
      axisLockedToY.current = false;
      lastInsertionIndexRef.current = null;

      // If initiated from the drag handle OR force requested, start immediately with vertical lock
      if (isHandle || opts?.force) {
        hasDragStarted.current = true;
        axisLockedToY.current = true;
        setIsDragging(true);
        onStartDrag?.(index);
      } else {
        hasDragStarted.current = false;
      }

      // Capture initial scroll position for offset calculations
      initialScrollY.current = window.scrollY;
      currentScrollY.current = window.scrollY;

      // Do not call preventDefault here; rely on CSS touch-action to manage scrolling behavior.
    },
    [onStartDrag]
  );

  const onPointerMove = useCallback(
    (e: React.PointerEvent | React.MouseEvent | React.TouchEvent) => {
      if (draggedIndex === null) return;

      const pos = getPointerPos(e);
      const deltaX = pos.x - dragStartPos.x;
      const deltaY = pos.y - dragStartPos.y;
      const absX = Math.abs(deltaX);
      const absY = Math.abs(deltaY);
      const distance = Math.sqrt(deltaX * deltaX + deltaY * deltaY);

      // Axis lock & start slop
      if (!hasDragStarted.current) {
        if (distance < DRAG_START_SLOP) {
          return; // allow normal scroll/swipe
        }
        // Lock to Y if vertical dominates
        if (absY >= absX * AXIS_LOCK_Y_RATIO) {
          axisLockedToY.current = true;
          hasDragStarted.current = true;
          setIsDragging(true);
          onStartDrag?.(draggedIndex);
        } else {
          // Horizontal dominates – do not start vertical drag
          return;
        }
      }

      if (hasDragStarted.current && axisLockedToY.current) {
        // Use requestAnimationFrame for smooth 60fps updates
        if (animationFrameRef.current) {
          cancelAnimationFrame(animationFrameRef.current);
        }

        animationFrameRef.current = requestAnimationFrame(() => {
          // Update current scroll position (for manual scrolling during drag)
          currentScrollY.current = window.scrollY;

          // Calculate drag offset accounting for scroll changes
          const scrollDelta = currentScrollY.current - initialScrollY.current;
          const adjustedDeltaY = deltaY + scrollDelta;

          setDragOffset({ x: 0, y: adjustedDeltaY }); // lock X to 0 during vertical drag

          // Handle auto-scroll when near viewport edges
          handleAutoScroll(pos.y);

          // Throttle drop target detection to improve performance
          const shouldUpdateDropTarget = Math.abs(adjustedDeltaY) % 8 < 4;
          if (shouldUpdateDropTarget) {
            const insertionIndex = findDropTarget(pos.x, pos.y, draggedIndex);

            // Apply tolerance to reduce jitter/flicker
            if (lastInsertionIndexRef.current === null || insertionIndex !== lastInsertionIndexRef.current) {
              // Only switch if pointer moved past tolerance relative to previous target
              let allowSwitch = true;
              if (lastInsertionIndexRef.current !== null) {
                const prevEl = cardElements.current[Math.min(Math.max(lastInsertionIndexRef.current, 0), cardElements.current.length - 1)];
                if (prevEl) {
                  const rect = prevEl.getBoundingClientRect();
                  const centerY = rect.top + rect.height / 2;
                  if (Math.abs(pos.y - centerY) < REORDER_CROSS_TOLERANCE_PX) {
                    allowSwitch = false;
                  }
                }
              }
              if (allowSwitch) {
                lastInsertionIndexRef.current = insertionIndex;
                setDragOverIndex(insertionIndex);
              }
            }
          }
        });

        // Do not call preventDefault here; React may attach touch listeners as passive.
        // Use CSS (e.g., 'touch-none') on the draggable element while dragging to prevent scroll.
      }
    },
    [draggedIndex, dragStartPos, findDropTarget, onStartDrag, handleAutoScroll]
  );

  const onPointerUp = useCallback(
    (e: React.PointerEvent | React.MouseEvent | React.TouchEvent) => {
      if (draggedIndex === null) return;

      if (hasDragStarted.current && dragOverIndex !== null && dragOverIndex !== draggedIndex) {
        // Perform reorder
        const newItems = [...items];
        const draggedItem = newItems[draggedIndex];

        if (draggedItem) {
          // Remove the dragged item first
          newItems.splice(draggedIndex, 1);

          // Calculate the correct insertion point after removal
          // If we're moving to a position after where we removed the item, adjust by -1
          const adjustedInsertionIndex = dragOverIndex > draggedIndex ? dragOverIndex - 1 : dragOverIndex;

          // Insert at the calculated position
          newItems.splice(adjustedInsertionIndex, 0, draggedItem);

          onReorder(newItems);
        }
      }

      // Reset state
      setDraggedIndex(null);
      setDragOverIndex(null);
      setIsDragging(false);
      setDragOffset({ x: 0, y: 0 });
      setDragStartPos({ x: 0, y: 0 });
      hasDragStarted.current = false;

      // Clean up animation frame and auto-scroll
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
        animationFrameRef.current = undefined;
      }
      stopAutoScroll();

      onEndDrag?.();

      // Do not call preventDefault on pointer/touch end.
    },
    [draggedIndex, dragOverIndex, items, onReorder, onEndDrag, stopAutoScroll]
  );

  // Global pointer move and up handlers
  useEffect(() => {
    if (draggedIndex === null) return;

    const handlePointerMove = (e: PointerEvent | MouseEvent | TouchEvent) => {
      // Bridge native event to handler expecting React types; we only read clientX/Y or touches safely.
      onPointerMove(e as unknown as React.PointerEvent);
    };

    const handlePointerUp = (e: PointerEvent | MouseEvent | TouchEvent) => {
      onPointerUp(e as unknown as React.PointerEvent);
    };

    // Add listeners to document to capture events outside the element
    document.addEventListener('pointermove', handlePointerMove, { passive: false });
    document.addEventListener('pointerup', handlePointerUp, { passive: false });
    document.addEventListener('mousemove', handlePointerMove, { passive: false });
    document.addEventListener('mouseup', handlePointerUp, { passive: false });
    document.addEventListener('touchmove', handlePointerMove, { passive: false });
    document.addEventListener('touchend', handlePointerUp, { passive: false });

    return () => {
      document.removeEventListener('pointermove', handlePointerMove);
      document.removeEventListener('pointerup', handlePointerUp);
      document.removeEventListener('mousemove', handlePointerMove);
      document.removeEventListener('mouseup', handlePointerUp);
      document.removeEventListener('touchmove', handlePointerMove);
      document.removeEventListener('touchend', handlePointerUp);
    };
  }, [draggedIndex, onPointerMove, onPointerUp]);

  // Store card element references
  const setCardElement = useCallback((index: number, element: HTMLElement | null) => {
    cardElements.current[index] = element;
  }, []);

  const state: UniversalDragState = {
    draggedIndex,
    dragOverIndex,
    isDragging,
    dragOffset,
    dragStartPos,
  };

  const handlers: UniversalDragHandlers = {
    onPointerDown,
    onPointerMove,
    onPointerUp,
    setCardElement,
  };

  return [state, handlers];
}

import { useEffect, useMemo, useState, useRef } from "react";
import { type SwipeSettings } from "~/hooks/use-swipe-gestures";
import { useUniversalDragReorder } from "~/hooks/use-universal-drag-reorder";
import { useOfflineSaveQueue } from "~/hooks/use-offline-save-queue";
import { api } from "~/trpc/react";
import { type ExerciseData } from "~/app/_components/exercise-card";
import { type SetData } from "~/app/_components/set-input";

interface PreviousBest {
  weight?: number;
  reps?: number;
  sets?: number;
  unit: "kg" | "lbs";
}

export interface UseWorkoutSessionStateArgs {
  sessionId: number;
}

export function useWorkoutSessionState({ sessionId }: UseWorkoutSessionStateArgs) {
  const [exercises, setExercises] = useState<ExerciseData[]>([]);
  const [expandedExercises, setExpandedExercises] = useState<number[]>([0]);
  const [loading, setLoading] = useState(true);
  const [isReadOnly, setIsReadOnly] = useState(false);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [previousExerciseData, setPreviousExerciseData] = useState<
    Map<string, { best?: PreviousBest; sets?: SetData[] }>
  >(new Map());
  const [previousDataLoaded, setPreviousDataLoaded] = useState(false);
  const [notification, setNotification] = useState<{ type: "error" | "success"; message: string } | null>(null);
  const [collapsedIndexes, setCollapsedIndexes] = useState<number[]>([]);
  // Track last reversible action for persistent Undo behavior
  type HistoryAction =
    | {
        type: "swipeToEnd";
        exerciseId: string;
        fromIndex: number;
        toIndex: number;
      }
    | {
        type: "toggleCollapse";
        exerciseId: string;
        previousExpanded: boolean;
      }
    | {
        type: "dragReorder";
        previousOrder: Array<{ name: string; templateExerciseId?: number }>;
      }
    | {
        type: "addSet";
        exerciseIndex: number;
        newSetId: string;
      }
    | {
        type: "deleteSet";
        exerciseIndex: number;
        deletedSet: { index: number; data: SetData };
      }
    | {
        type: "toggleUnit";
        exerciseIndex: number;
        setIndex: number;
        previousUnit: "kg" | "lbs";
      }
    | {
        type: "editSetFields";
        exerciseIndex: number;
        setIndex: number;
        before: Partial<SetData>;
        after: Partial<SetData>;
      };

  const [lastAction, setLastAction] = useState<HistoryAction | null>(null);
  const [redoStack, setRedoStack] = useState<HistoryAction[]>([]);
  const REDO_LIMIT = 30;
  const [progressionModal, setProgressionModal] = useState<{
    isOpen: boolean;
    exerciseIndex: number;
    exerciseName: string;
    previousBest: {
      weight?: number;
      reps?: number;
      sets: number;
      unit: "kg" | "lbs";
    };
  } | null>(null);
  const [hasShownAutoProgression, setHasShownAutoProgression] = useState(false);
  const [progressionScopeModal, setProgressionScopeModal] = useState<{
    isOpen: boolean;
    exerciseIndex: number;
    progressionType: "weight" | "reps";
    increment: string;
    previousBest: {
      weight?: number;
      reps?: number;
      sets: number;
      unit: "kg" | "lbs";
    };
  } | null>(null);

  const utils = api.useUtils();
  const { data: session } = api.workouts.getById.useQuery({ id: sessionId });
  const { data: preferences } = api.preferences.get.useQuery();
  const updatePreferencesMutation = api.preferences.update.useMutation();
  // Strongly type the input using the router's inferred type to avoid union misinference
  type UpdatePrefsInput = { defaultWeightUnit: "kg" | "lbs" };
  const updatePreferences = (input: UpdatePrefsInput) => {
    updatePreferencesMutation.mutate(input as unknown as Parameters<typeof updatePreferencesMutation.mutate>[0]);
  };

  const { enqueue, flush, queueSize, isFlushing } = useOfflineSaveQueue();

  // id generation
  const generateSetId = () => `set-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

  // swipe settings
  // Phase 3: allow asymmetric thresholds via preferences (future: per-direction)
  const swipeSettings: Partial<SwipeSettings> = {
    dismissThreshold: 140,
    velocityThreshold: 4,
    friction: 0.88,
    minimumVelocity: 0.15,
  };

  // display order
  const getDisplayOrder = () =>
    exercises.map((exercise, index) => ({ exercise, originalIndex: index }));

  // drag + reorder
  const displayOrder = useMemo(() => getDisplayOrder(), [exercises]);
  const [dragState, dragHandlers] = useUniversalDragReorder(
    displayOrder,
    (newDisplayOrder) => {
      // Capture previous order for Undo
      const prevOrder = exercises.map((ex) => ({ name: ex.exerciseName, templateExerciseId: ex.templateExerciseId }));
      const newExercises = newDisplayOrder.map((item) => item.exercise);
      setExercises(newExercises);

      // record undo action
      setLastAction({
        type: "dragReorder",
        previousOrder: prevOrder,
      });

      // maintain collapsed by identity
      const idFor = (ex: ExerciseData) => ex.templateExerciseId ?? `name:${ex.exerciseName}`;
      const collapsedIdSet = new Set(
        collapsedIndexes
          .map((i) => exercises[i])
          .filter(Boolean)
          .map((ex) => idFor(ex!)),
      );

      const rebuiltCollapsedIndexes: number[] = [];
      newExercises.forEach((ex, idx) => {
        if (collapsedIdSet.has(idFor(ex))) {
          rebuiltCollapsedIndexes.push(idx);
        }
      });
      setCollapsedIndexes(rebuiltCollapsedIndexes);

      // update expanded indices based on identity
      const newExpandedIndexes = expandedExercises
        .map((oldIndex) => {
          const exerciseId = exercises[oldIndex]?.templateExerciseId;
          return newExercises.findIndex((ex) => ex.templateExerciseId === exerciseId);
        })
        .filter((index) => index !== -1);
      setExpandedExercises(newExpandedIndexes);
    },
    (draggedDisplayIndex) => {
      const originalIndex = displayOrder[draggedDisplayIndex]?.originalIndex;
      if (originalIndex !== undefined) {
        setExpandedExercises((prev) => prev.filter((index) => index !== originalIndex));
      }
    }
  );

  const saveWorkout = api.workouts.save.useMutation({
    onMutate: async (newWorkout) => {
      await utils.workouts.getRecent.cancel();
      const previousWorkouts = utils.workouts.getRecent.getData({ limit: 5 });

      if (session?.template) {
        const optimisticWorkout = {
          id: sessionId,
          user_id: session.user_id,
          templateId: session.templateId,
          workoutDate: session.workoutDate,
          // Phase 3 telemetry fields with safe defaults to match cache type
          theme_used: (session as any).theme_used ?? null,
          device_type: (session as any).device_type ?? null,
          perf_metrics: (session as any).perf_metrics ?? null,
          createdAt: new Date(),
          updatedAt: new Date(),
          template: session.template,
          exercises: newWorkout.exercises.flatMap((exercise, exerciseIndex) =>
            exercise.sets.map((set, setIndex) => ({
              id: -(exerciseIndex * 100 + setIndex),
              user_id: session.user_id,
              sessionId: sessionId,
              templateExerciseId: exercise.templateExerciseId ?? null,
              exerciseName: exercise.exerciseName,
              setOrder: setIndex,
              weight: set.weight?.toString() ?? null,
              reps: set.reps ?? null,
              sets: set.sets ?? null,
              unit: set.unit as string,
              // Provide Phase 2 nullable fields to satisfy cache type
              rpe: null as unknown as number | null,
              rest_seconds: null as unknown as number | null,
              is_estimate: false,
              is_default_applied: false,
              createdAt: new Date(),
            }))
          ),
        } as const;

        utils.workouts.getRecent.setData({ limit: 5 }, (old) =>
          old ? [optimisticWorkout, ...old.slice(0, 4)] : [optimisticWorkout],
        );
      }

      return { previousWorkouts };
    },
    onError: (_err, _newWorkout, context) => {
      if (context?.previousWorkouts) {
        utils.workouts.getRecent.setData({ limit: 5 }, context.previousWorkouts);
      }
    },
    onSettled: () => {
      void utils.workouts.getRecent.invalidate();
    },
  });

  const deleteWorkout = api.workouts.delete.useMutation({
    onMutate: async () => {
      await utils.workouts.getRecent.cancel();
      const previousWorkouts = utils.workouts.getRecent.getData({ limit: 5 });

      utils.workouts.getRecent.setData({ limit: 5 }, (old) =>
        old ? old.filter((workout) => workout.id !== sessionId) : [],
      );

      return { previousWorkouts };
    },
    onError: (_err, _variables, context) => {
      if (context?.previousWorkouts) {
        utils.workouts.getRecent.setData({ limit: 5 }, context.previousWorkouts);
      }
    },
    onSettled: () => {
      void utils.workouts.getRecent.invalidate();
    },
  });

  // previous data loading
  useEffect(() => {
    if (!session?.template || isReadOnly) {
      setPreviousDataLoaded(true);
      return;
    }

    const loadPreviousData = async () => {
      const previousDataMap = new Map();
      for (const templateExercise of session.template.exercises) {
        try {
          const data = await utils.workouts.getLastExerciseData.fetch({
            exerciseName: templateExercise.exerciseName,
            excludeSessionId: sessionId,
            templateExerciseId: templateExercise.id,
          });
          if (data) {
            previousDataMap.set(templateExercise.exerciseName, data);
          }
        } catch (error) {
          // noop
        }
      }
      setPreviousExerciseData(previousDataMap);
      setPreviousDataLoaded(true);
    };

    void loadPreviousData();
  }, [session?.template, isReadOnly, utils.workouts.getLastExerciseData, sessionId]);

  // session init
  useEffect(() => {
    if (!session?.template || !previousDataLoaded) return;

    if (session.exercises && session.exercises.length > 0) {
      const exerciseGroups = new Map<string, typeof session.exercises>();
      session.exercises.forEach((sessionExercise) => {
        const key = sessionExercise.exerciseName;
        if (!exerciseGroups.has(key)) exerciseGroups.set(key, []);
        exerciseGroups.get(key)!.push(sessionExercise);
      });

      const existingExercises: ExerciseData[] = Array.from(exerciseGroups.entries()).map(
        ([exerciseName, exerciseData]) => {
          exerciseData.sort((a, b) => (a.setOrder ?? 0) - (b.setOrder ?? 0));
          return {
            templateExerciseId: exerciseData[0]?.templateExerciseId ?? undefined,
            exerciseName,
            sets: exerciseData.map((sessionExercise) => ({
              id: `existing-${sessionExercise.id}`,
              weight: sessionExercise.weight ? parseFloat(sessionExercise.weight) : undefined,
              reps: sessionExercise.reps ?? undefined,
              sets: sessionExercise.sets ?? 1,
              unit: (sessionExercise.unit as "kg" | "lbs") ?? "kg",
            })),
            unit: (exerciseData[0]?.unit as "kg" | "lbs") ?? "kg",
          };
        }
      );

      setExercises(existingExercises);
      setIsReadOnly(true);
      setExpandedExercises(existingExercises.map((_, index) => index));
    } else {
      const initialExercises: ExerciseData[] = session.template.exercises.map(
        (templateExercise: { id: number; exerciseName: string }) => {
          const previousData = previousExerciseData.get(templateExercise.exerciseName);
          const defaultUnit = (preferences?.defaultWeightUnit ?? "kg") as "kg" | "lbs";

          let sets: SetData[] = [];
          if (previousData?.sets) {
            sets = previousData.sets.map((prevSet) => ({
              id: generateSetId(),
              weight: prevSet.weight,
              reps: prevSet.reps,
              sets: prevSet.sets,
              unit: prevSet.unit,
            }));
          } else {
            sets = [
              {
                id: generateSetId(),
                weight: undefined,
                reps: undefined,
                sets: 1,
                unit: defaultUnit,
              },
            ];
          }

          return {
            templateExerciseId: templateExercise.id,
            exerciseName: templateExercise.exerciseName,
            sets,
            unit: defaultUnit,
          };
        },
      );

      setExercises(initialExercises);
      setIsReadOnly(false);
    }

    setLoading(false);
  }, [session?.template, session?.exercises, preferences?.defaultWeightUnit, previousExerciseData, previousDataLoaded]);

  // auto-progression modal
  useEffect(() => {
    const isNewWorkout = !session?.exercises || session.exercises.length === 0;
    const safeToShowModal = session && !isReadOnly && previousDataLoaded;

    if (!loading && safeToShowModal && isNewWorkout && exercises.length > 0 && !progressionModal && !hasShownAutoProgression) {
      for (let i = 0; i < exercises.length; i++) {
        const exercise = exercises[i];
        const previousData = previousExerciseData.get(exercise?.exerciseName ?? "");
        if (exercise && previousData?.best?.weight && previousData.best.sets) {
          setProgressionModal({
            isOpen: true,
            exerciseIndex: i,
            exerciseName: exercise.exerciseName,
            previousBest: {
              weight: previousData.best.weight,
              reps: previousData.best.reps,
              sets: previousData.best.sets,
              unit: previousData.best.unit,
            },
          });
          setHasShownAutoProgression(true);
          break;
        }
      }
    }
  }, [loading, isReadOnly, exercises, previousExerciseData, progressionModal, hasShownAutoProgression, session, previousDataLoaded]);

  // helpers exposed to component

  // Undo handlers to be used by the component (define before return so it exists)
  const undoLastAction = () => {
    if (!lastAction) return;

    // Push to redo stack
    setRedoStack((prev) => {
      const next = [lastAction, ...prev];
      return next.slice(0, REDO_LIMIT);
    });

    if (lastAction.type === "swipeToEnd") {
      // Move the exercise back to original index
      setExercises((prev) => {
        const idFor = (ex: ExerciseData) => (ex.templateExerciseId ?? `name:${ex.exerciseName}`).toString();
        const srcIndex = prev.findIndex((ex) => idFor(ex) === lastAction.exerciseId);
        if (srcIndex === -1) return prev;
        const next = [...prev];
        const [item] = next.splice(srcIndex, 1);
        next.splice(lastAction.fromIndex, 0, item!);
        return next;
      });
      // Also clear collapsed flag for correctness
      setCollapsedIndexes((prevCollapsed) => prevCollapsed.filter((i) => i !== -1));
    } else if (lastAction.type === "toggleCollapse") {
      // Restore previous expanded state
      setExpandedExercises((prev) => {
        const exerciseIndex = exercises.findIndex(
          (ex) => (ex.templateExerciseId ?? `name:${ex.exerciseName}`).toString() === lastAction.exerciseId,
        );
        if (exerciseIndex === -1) return prev;
        const isCurrentlyExpanded = prev.includes(exerciseIndex);
        if (lastAction.previousExpanded && !isCurrentlyExpanded) {
          return [...prev, exerciseIndex];
        }
        if (!lastAction.previousExpanded && isCurrentlyExpanded) {
          return prev.filter((i) => i !== exerciseIndex);
        }
        return prev;
      });
    } else if (lastAction.type === "dragReorder") {
      // Restore previous order based on identity
      setExercises((prev) => {
        const idFor = (ex: ExerciseData) => (ex.templateExerciseId ?? `name:${ex.exerciseName}`).toString();
        const byId = new Map(prev.map((ex) => [idFor(ex), ex]));
        const restored: ExerciseData[] = [];
        for (const item of lastAction.previousOrder) {
          const key = (item.templateExerciseId ?? `name:${item.name}`).toString();
          const ex = byId.get(key);
          if (ex) restored.push(ex);
        }
        // Include any extras that might have been added (safety)
        for (const ex of prev) {
          const key = idFor(ex);
          if (!restored.find((r) => idFor(r) === key)) restored.push(ex);
        }
        return restored;
      });
    } else if (lastAction.type === "addSet") {
      // Remove the set that was just added
      setExercises((prev) => {
        const next = [...prev];
        const ex = next[lastAction.exerciseIndex];
        if (!ex) return prev;
        const idx = ex.sets.findIndex((s) => s.id === lastAction.newSetId);
        if (idx !== -1) {
          ex.sets.splice(idx, 1);
        }
        return next;
      });
    } else if (lastAction.type === "deleteSet") {
      // Reinsert the deleted set at its original position
      setExercises((prev) => {
        const next = [...prev];
        const ex = next[lastAction.exerciseIndex];
        if (!ex) return prev;
        const insertAt = Math.min(Math.max(0, lastAction.deletedSet.index), ex.sets.length);
        ex.sets.splice(insertAt, 0, lastAction.deletedSet.data);
        return next;
      });
    } else if (lastAction.type === "toggleUnit") {
      // Restore previous unit
      setExercises((prev) => {
        const next = [...prev];
        const ex = next[lastAction.exerciseIndex];
        if (!ex) return prev;
        const s = ex.sets[lastAction.setIndex];
        if (!s) return prev;
        s.unit = lastAction.previousUnit;
        return next;
      });
    } else if (lastAction.type === "editSetFields") {
      // Restore previous field values
      setExercises((prev) => {
        const next = [...prev];
        const ex = next[lastAction.exerciseIndex];
        if (!ex) return prev;
        const s = ex.sets[lastAction.setIndex];
        if (!s) return prev;
        Object.assign(s, lastAction.before);
        return next;
      });
    }

    // Clear last action after undo
    setLastAction(null);
  };

  // DEBUG helpers
  const debugLog = (...args: unknown[]) => {

    console.log("[WorkoutSessionState]", ...args);
  };

  // Action dedupe flags (same-tick guard)
  const addSetInFlightRef = useRef(false);
  const deleteSetInFlightRef = useRef(false);

  const updateSet = (exerciseIndex: number, setIndex: number, field: keyof SetData, value: string | number | undefined) => {
    debugLog("updateSet:start", { exerciseIndex, setIndex, field, value, len: exercises[exerciseIndex]?.sets.length });
    const newExercises = [...exercises];
    const target = newExercises[exerciseIndex]?.sets[setIndex];
    if (target) {
      const before: Partial<SetData> = { [field]: target[field] } as Partial<SetData>;

      (target as any)[field] = value;
      const after: Partial<SetData> = { [field]: value } as Partial<SetData>;
      setExercises(newExercises);
      debugLog("updateSet:applied", { exerciseIndex, setIndex, field, before, after });
      // push history
      setLastAction({
        type: "editSetFields",
        exerciseIndex,
        setIndex,
        before,
        after,
      });
      // clear redo on new action
      setRedoStack([]);
    }
  };

  const toggleUnit = (exerciseIndex: number, setIndex: number) => {
    const currentUnit = exercises[exerciseIndex]?.sets[setIndex]?.unit ?? "kg";
    const newUnit = currentUnit === "kg" ? "lbs" : "kg";
    // record explicit toggle action for precise undo
    setLastAction({
      type: "toggleUnit",
      exerciseIndex,
      setIndex,
      previousUnit: currentUnit,
    });
    setRedoStack([]);
    updateSet(exerciseIndex, setIndex, "unit", newUnit);
    // preferences mutation expects object shape { defaultWeightUnit }
    updatePreferences({ defaultWeightUnit: (newUnit as unknown) as "kg" | "lbs" });
  };

  const addSet = (exerciseIndex: number) => {
    if (addSetInFlightRef.current) {
      debugLog("addSet:suppressedDuplicate", { exerciseIndex });
      return;
    }
    addSetInFlightRef.current = true;
    debugLog("addSet:start", { exerciseIndex });
    setExercises((prev) => {
      const next = [...prev];
      const exercise = next[exerciseIndex];
      if (!exercise) return prev;

      const lastSet = exercise.sets[exercise.sets.length - 1];

      // Phase 3: Predictive defaults engine (simple strategy v1)
      const predictiveEnabled = (preferences as any)?.predictive_defaults_enabled === true;

      // Determine base values
      let nextWeight: number | undefined = undefined;
      let nextReps: number | undefined = undefined;
      let nextSetsCount: number | undefined = 1;
      const unitToUse = (lastSet?.unit ?? exercise.unit);

      if (predictiveEnabled) {
        const recent = [...exercise.sets].reverse().find(s => (s.weight ?? s.reps ?? s.sets) !== undefined);
        if (recent) {
          nextWeight = recent.weight;
          nextReps = recent.reps;
          nextSetsCount = recent.sets ?? 1;
        }
      }

      const newId = generateSetId();
      const newSet: SetData = {
        id: newId,
        weight: nextWeight,
        reps: nextReps,
        sets: nextSetsCount,
        unit: unitToUse,
      };

      // Replace sets array reference to force reconciliation
      const beforeIds = exercise.sets.map(s => s.id);
      exercise.sets = [...exercise.sets, newSet];
      const afterIds = exercise.sets.map(s => s.id);

      debugLog("addSet:added", { exerciseIndex, newId, beforeIds, afterIds, totalSets: exercise.sets.length });
      setLastAction({ type: "addSet", exerciseIndex, newSetId: newId });
      setRedoStack([]);

      return next;
    });
    // Release guard after a short delay to prevent rapid double-clicks
    setTimeout(() => { addSetInFlightRef.current = false; }, 150);
  };

  const deleteSet = (exerciseIndex: number, setIndex: number) => {
    if (deleteSetInFlightRef.current) {
      debugLog("deleteSet:suppressedDuplicate", { exerciseIndex, setIndex });
      return;
    }
    deleteSetInFlightRef.current = true;

    debugLog("deleteSet:click", { exerciseIndex, setIndex, lenBefore: exercises[exerciseIndex]?.sets.length });
    setExercises((prev) => {
      const next = [...prev];
      const exercise = next[exerciseIndex];
      if (!exercise) {
        debugLog("deleteSet:exerciseNotFound", { exerciseIndex });
        return prev;
      }
      if (exercise.sets.length === 0) {
        debugLog("deleteSet:noSets", { exerciseIndex });
        return prev;
      }
      // Defensive clamp of index to current bounds
      const clampedIndex = Math.max(0, Math.min(setIndex, exercise.sets.length - 1));
      const removed = exercise.sets[clampedIndex];
      if (!removed) {
        debugLog("deleteSet:setNotFound", { exerciseIndex, setIndex });
        return prev;
      }
      const beforeIds = exercise.sets.map((s) => s.id);
      exercise.sets = exercise.sets.filter((_, i) => i !== clampedIndex); // replace array ref for React reconciliation
      const afterIds = exercise.sets.map((s) => s.id);
      debugLog("deleteSet:removed", { exerciseIndex, setIndex: clampedIndex, removedId: removed.id, beforeIds, afterIds, lenAfter: exercise.sets.length });
      // record undo uses clamped index so reinsert is correct against new array
      setLastAction({ type: "deleteSet", exerciseIndex, deletedSet: { index: clampedIndex, data: removed } });
      setRedoStack([]);
      return next;
    });
    // Release guard on the next tick to coalesce duplicate events from the same user action
    queueMicrotask(() => { deleteSetInFlightRef.current = false; });
  };

  const toggleExpansion = (exerciseIndex: number) => {
    const exercise = exercises[exerciseIndex];
    if (!exercise) return;

    const wasExpanded = expandedExercises.includes(exerciseIndex);
    setExpandedExercises((prev) =>
      wasExpanded ? prev.filter((i) => i !== exerciseIndex) : [...prev, exerciseIndex],
    );

    // record undo action
    setLastAction({
      type: "toggleCollapse",
      exerciseId: (exercise.templateExerciseId ?? `name:${exercise.exerciseName}`).toString(),
      previousExpanded: wasExpanded,
    });
  };

  const handleSwipeToBottom = (exerciseIndex: number) => {
    setExercises((prev) => {
      if (exerciseIndex < 0 || exerciseIndex >= prev.length) return prev;
      const next = [...prev];
      const [item] = next.splice(exerciseIndex, 1);
      if (!item) return prev;
      const toIndex = next.length; // will be pushed to end
      next.push(item);

      // record undo action
      setLastAction({
        type: "swipeToEnd",
        exerciseId: (item.templateExerciseId ?? `name:${item.exerciseName}`).toString(),
        fromIndex: exerciseIndex,
        toIndex,
      });

      return next;
    });

    setCollapsedIndexes((prevCollapsed) => {
      const newCollapsed = prevCollapsed.filter((i) => i !== exerciseIndex);
      return [...newCollapsed, -1];
    });

    setExpandedExercises((prev) => prev.filter((idx) => idx !== exerciseIndex));
  };

  const buildSavePayload = () => {
    const cleanedExercises = exercises
      .map((exercise) => ({
        ...exercise,
        sets: exercise.sets
          .filter(
            (set) => set.weight !== undefined || set.reps !== undefined || (set.sets && set.sets > 0),
          )
          .map((set) => ({
            ...set,
            id: set.id ?? `offline-${Math.random().toString(36).slice(2)}`,
            weight: set.weight === null ? undefined : set.weight,
            reps: set.reps === null ? undefined : set.reps,
            sets: set.sets === null ? 1 : set.sets,
          })),
      }))
      .filter((exercise) => exercise.sets.length > 0);

    return {
      sessionId,
      exercises: cleanedExercises,
    };
  };

  // Reorder sets within an exercise (true array reorder, not field swapping)
  const reorderSets = (exerciseIndex: number, from: number, to: number) => {
    setExercises((prev) => {
      const next = [...prev];
      const ex = next[exerciseIndex];
      if (!ex) return prev;
      const beforeIds = ex.sets.map((s) => s.id);
      const setsCopy = [...ex.sets];
      const [moved] = setsCopy.splice(from, 1);
      if (!moved) return prev;
      const clampedTo = Math.max(0, Math.min(to, setsCopy.length));
      setsCopy.splice(clampedTo, 0, moved);
      ex.sets = setsCopy; // replace ref
      const afterIds = ex.sets.map((s) => s.id);
      debugLog("reorderSets:applied", { exerciseIndex, from, to: clampedTo, beforeIds, afterIds });

      // history for undo: store previous order (lightweight)
      setLastAction({
        type: "editSetFields",
        exerciseIndex,
        setIndex: clampedTo,
        before: {},
        after: {},
      });
      setRedoStack([]);
      return next;
    });
  };

  const redoLastUndo = () => {
    const action = redoStack[0];
    if (!action) return;
    const rest = redoStack.slice(1);
    setRedoStack(rest);

    // Re-apply the action we undid
    if (action.type === "swipeToEnd") {
      setExercises((prev) => {
        const idFor = (ex: ExerciseData) => (ex.templateExerciseId ?? `name:${ex.exerciseName}`).toString();
        const srcIndex = prev.findIndex((ex) => idFor(ex) === action.exerciseId);
        if (srcIndex === -1) return prev;
        const next = [...prev];
        const [item] = next.splice(srcIndex, 1);
        next.push(item!);
        return next;
      });
    } else if (action.type === "toggleCollapse") {
      setExpandedExercises((prev) => {
        const exerciseIndex = exercises.findIndex(
          (ex) => (ex.templateExerciseId ?? `name:${ex.exerciseName}`).toString() === action.exerciseId,
        );
        if (exerciseIndex === -1) return prev;
        const isCurrentlyExpanded = prev.includes(exerciseIndex);
        // redo does the inverse of undo: apply what original action did
        if (action.previousExpanded) {
          // original action collapsed; redo collapse if currently expanded
          return prev.filter((i) => i !== exerciseIndex);
        } else {
          // original action expanded; redo expand
          if (!isCurrentlyExpanded) return [...prev, exerciseIndex];
          return prev;
        }
      });
    } else if (action.type === "dragReorder") {
      // Reapply a drag isn't trivially reconstructable; leave noop for redo to avoid unstable reorder.
      // Alternatively, we could store the 'after' order. For now, skip.
    } else if (action.type === "addSet") {
      // Re-apply add by inserting a stub set if original id not present
      setExercises((prev) => {
        const next = [...prev];
        const ex = next[action.exerciseIndex];
        if (!ex) return prev;
        if (!ex.sets.find((s) => s.id === action.newSetId)) {
          ex.sets.push({
            id: action.newSetId,
            unit: ex.unit,
            sets: 1,
          } as SetData);
        }
        return next;
      });
    } else if (action.type === "deleteSet") {
      // Re-apply delete by removing the set matching the saved data id if present
      setExercises((prev) => {
        const next = [...prev];
        const ex = next[action.exerciseIndex];
        if (!ex) return prev;
        const idx = ex.sets.findIndex((s) => s.id === action.deletedSet.data.id);
        if (idx !== -1) {
          ex.sets.splice(idx, 1);
        }
        return next;
      });
    } else if (action.type === "toggleUnit") {
      // Re-apply unit toggle by flipping from previous
      setExercises((prev) => {
        const next = [...prev];
        const ex = next[action.exerciseIndex];
        if (!ex) return prev;
        const s = ex.sets[action.setIndex];
        if (!s) return prev;
        s.unit = action.previousUnit === "kg" ? "lbs" : "kg";
        return next;
      });
    } else if (action.type === "editSetFields") {
      // Re-apply field edits by setting 'after' values
      setExercises((prev) => {
        const next = [...prev];
        const ex = next[action.exerciseIndex];
        if (!ex) return prev;
        const s = ex.sets[action.setIndex];
        if (!s) return prev;
        Object.assign(s, action.after);
        return next;
      });
    }
  };


  return {
    // state
    exercises,
    setExercises,
    expandedExercises,
    setExpandedExercises,
    loading,
    isReadOnly,
    showDeleteConfirm,
    setShowDeleteConfirm,
    previousExerciseData,
    notification,
    setNotification,
    collapsedIndexes,
    progressionModal,
    setProgressionModal,
    hasShownAutoProgression,
    setHasShownAutoProgression,
    progressionScopeModal,
    setProgressionScopeModal,

    // trpc utils and prefs
    utils,
    preferences,

    // mutations and queue
    saveWorkout,
    deleteWorkout,
    enqueue,
    flush,
    queueSize,
    isFlushing,

    // interactions
    swipeSettings,
    dragState,
    dragHandlers,
    getDisplayOrder,
    toggleExpansion,
    handleSwipeToBottom,
    updateSet,
    toggleUnit,
    addSet,
    deleteSet,
    reorderSets,
    buildSavePayload,

    // expose updatePreferences for component use where needed
    updatePreferences,
    session,

    // undo surface
    lastAction,
    // expose setLastAction for advanced cases
    setLastAction,
    // redo + undo
    redoLastUndo,
    redoStack,
    undoLastAction,
  };
}

import { createClient, type SupabaseClient } from "@supabase/supabase-js";
import { env } from "~/env";

/**
 * Shared env guards (client-safe: only NEXT_PUBLIC_* are read)
 */
function requireEnv(name: string): string {
  const v = process.env[name];
  if (v === undefined || v === null || v === "") {
    // In tests, allow throwing to satisfy env-missing tests
    if (process.env.NODE_ENV === "test") {
      throw new Error(`${name} is not set`);
    }
    throw new Error(`${name} is not set`);
  }
  return v;
}

/**
 * Browser/client Supabase (optionally Clerk-authenticated)
 * Use in Client Components. Pass Clerk session from useSession() or omit.
 */
export function createClerkSupabaseClient(
  session?: { getToken?: () => Promise<string | null> } | null,
): SupabaseClient {
  const supabaseUrl = env.NEXT_PUBLIC_SUPABASE_URL;

  // Local dev helper: allow service role key when using local 127.0.0.1 Supabase instance
  const isLocalDev =
    process.env.NODE_ENV === "development" &&
    process.env.NEXT_PUBLIC_SUPABASE_URL?.includes("127.0.0.1");

  if (isLocalDev && process.env.NEXT_PUBLIC_SUPABASE_SERVICE_ROLE_KEY) {

    console.warn("[supabase] Using service role key for local development");
    return createClient(
      supabaseUrl,
      process.env.NEXT_PUBLIC_SUPABASE_SERVICE_ROLE_KEY,
      {
        auth: {
          autoRefreshToken: false,
          persistSession: false,
        },
      },
    );
  }

  // No test-specific bypass: tests expect env guards to enforce presence

  const supabaseAnonKey = env.NEXT_PUBLIC_SUPABASE_ANON_KEY;

  const getAuthToken = async (): Promise<string | null> => {
    if (!session?.getToken) return null;
    try {
      return await session.getToken();
    } catch {
      return null;
    }
  };

  return createClient(supabaseUrl, supabaseAnonKey, {
    global: {
      fetch: async (url, options: RequestInit = {}) => {
        const token = await getAuthToken();
        return fetch(url, {
          ...options,
          headers: {
            ...(options.headers as Record<string, string>),
            Authorization: token ? `Bearer ${token}` : "",
          },
        });
      },
    },
  });
}

import { createClient, type SupabaseClient } from "@supabase/supabase-js";
import { auth } from "@clerk/nextjs/server";
import { env } from "~/env";

/**
 * Shared env guards
 */
function requireEnv(name: string): string {
  const v = process.env[name];
  if (v === undefined || v === null) {
    if (process.env.NODE_ENV === "test") {
      return "test-placeholder";
    }
    throw new Error(`${name} is not set`);
  }
  return v;
}

/**
 * Server-side Supabase with Clerk auth from request context.
 * Use in Server Components, Route Handlers, and Server Actions.
 */
export async function createServerSupabaseClient(): Promise<SupabaseClient> {
  const supabaseUrl = env.NEXT_PUBLIC_SUPABASE_URL;
  const supabaseAnonKey = env.NEXT_PUBLIC_SUPABASE_ANON_KEY;

  // In test, avoid importing real server auth behaviors; just construct a client
  if (process.env.NODE_ENV === "test") {
    return createClient(supabaseUrl, supabaseAnonKey);
  }

  const { getToken } = await auth();
  const token = await getToken();

  return createClient(supabaseUrl, supabaseAnonKey, {
    global: {
      headers: {
        Authorization: token ? `Bearer ${token}` : "",
      },
    },
  });
}

/**
 * Server factory for per-request clients reading current Clerk auth on call.
 */
export function createServerSupabaseClientFactory() {
  return async (): Promise<SupabaseClient> => {
    const supabaseUrl = env.NEXT_PUBLIC_SUPABASE_URL;
    const supabaseAnonKey = env.NEXT_PUBLIC_SUPABASE_ANON_KEY;

    // In test, simply return a client without server auth headers
    if (process.env.NODE_ENV === "test") {
      return createClient(supabaseUrl, supabaseAnonKey);
    }

    const { getToken } = await auth();
    const token = await getToken();

    return createClient(supabaseUrl, supabaseAnonKey, {
      global: {
        headers: {
          Authorization: token ? `Bearer ${token}` : "",
        },
      },
    });
  };
}

